// utils.h
#pragma once
#include <vector>
#include <string>
#include <fstream>
#include <iostream>
#include <sstream>
#include <cmath>

struct Dataset {
    std::vector<std::vector<float>> data;
    int dim;
    int n;
};

struct Graph {
    std::vector<std::vector<int>> neighbors; // graph[i] = список индексов соседей i-го вектора
    int k;
    int n;
};

// Чтение CSV (только числа, одна строка на вектор)
Dataset load_dataset(const std::string& filename) {
    Dataset ds;
    std::ifstream file(filename);
    std::string line;

    if (!file.is_open()) {
        throw std::runtime_error("Could not open file: " + filename);
    }

    while (std::getline(file, line)) {
        std::stringstream ss(line);
        std::vector<float> row;
        float val;
        while (ss >> val) {
            row.push_back(val);
            if (ss.peek() == ',') ss.ignore();
        }
        if (!row.empty()) {
            ds.data.push_back(row);
        }
    }

    if (!ds.data.empty()) {
        ds.dim = ds.data[0].size();
        ds.n = ds.data.size();
    }
    return ds;
}

// Сохранение графа
void save_graph(const Graph& graph, const std::string& filename) {
    std::ofstream file(filename);
    file << graph.n << " " << graph.k << "\n";
    for (int i = 0; i < graph.n; ++i) {
        for (int j = 0; j < graph.k; ++j) {
            file << graph.neighbors[i][j];
            if (j < graph.k - 1) file << " ";
        }
        file << "\n";
    }
    file.close();
}

// Загрузка графа
Graph load_graph(const std::string& filename) {
    Graph graph;
    std::ifstream file(filename);
    int n, k;
    file >> n >> k;
    graph.n = n;
    graph.k = k;
    graph.neighbors.resize(n, std::vector<int>(k));

    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < k; ++j) {
            file >> graph.neighbors[i][j];
        }
    }
    return graph;
}

// Евклидово расстояние между двумя векторами
float euclidean_distance(const std::vector<float>& a, const std::vector<float>& b) {
    float sum = 0.0f;
    for (size_t i = 0; i < a.size(); ++i) {
        float diff = a[i] - b[i];
        sum += diff * diff;
    }
    return std::sqrt(sum);
}
