// CUDA kernel для вычисления расстояний (простой пример)
__global__ void compute_distances_kernel(const float* data, int n, int dim, int query_idx,
                                        float* distances, int* indices) {
    int i = blockIdx.x * blockDim.x + threadIdx.x;
    if (i >= n) return;

    float dist = 0.0f;
    int offset_query = query_idx * dim;
    int offset_i = i * dim;

    for (int d = 0; d < dim; ++d) {
        float diff = data[offset_query + d] - data[offset_i + d];
        dist += diff * diff;
    }
    dist = sqrtf(dist);

    distances[i] = dist;
    indices[i] = i;
}

// Сортировка на GPU с помощью Thrust
void sort_by_distance(float* d_distances, int* d_indices, int n) {
    thrust::device_ptr<float> dist_ptr(d_distances);
    thrust::device_ptr<int> idx_ptr(d_indices);

    thrust::sort(thrust::device, thrust::make_zip_iterator(thrust::make_tuple(dist_ptr, idx_ptr)),
                 thrust::make_zip_iterator(thrust::make_tuple(dist_ptr + n, idx_ptr + n)),
                 [] __device__ (const thrust::tuple<float, int>& a, const thrust::tuple<float, int>& b) {
                     return thrust::get<0>(a) < thrust::get<0>(b);
                 });
}

// Вспомогательная функция для проверки ошибок CUDA
#define CUDA_CHECK(call) \
    do { \
        cudaError_t error = call; \
        if (error != cudaSuccess) { \
            std::cerr << "CUDA error at " << __FILE__ << ":" << __LINE__ << " - " << cudaGetErrorString(error) << std::endl; \
            exit(1); \
        } \
    } while(0)

KNNWrapper::KNNWrapper(int k, bool verbose, bool debug)
    : k(k), verbose(verbose), debug(debug), d_data(nullptr) {}

