    if (d_data) {
        CUDA_CHECK(cudaFree(d_data));
    }
}

void KNNWrapper::load_dataset(const std::string& filename) {
    dataset = ::load_dataset(filename);
    if (verbose) {
        std::cout << "Loaded dataset: " << dataset.n << " points, dim = " << dataset.dim << std::endl;
    }

    // Копируем данные на GPU
    size_t data_size = dataset.n * dataset.dim * sizeof(float);
    CUDA_CHECK(cudaMalloc(&d_data, data_size));
    CUDA_CHECK(cudaMemcpy(d_data, dataset.data[0].data(), data_size, cudaMemcpyHostToDevice));
}

void KNNWrapper::build_graph() {
    graph.n = dataset.n;
    graph.k = k;
    graph.neighbors.resize(dataset.n, std::vector<int>(k));

    float* d_distances;
    int* d_indices;
    CUDA_CHECK(cudaMalloc(&d_distances, dataset.n * sizeof(float)));
    CUDA_CHECK(cudaMalloc(&d_indices, dataset.n * sizeof(int)));

    float* h_distances = new float[dataset.n];
    int* h_indices = new int[dataset.n];

    const float* data_ptr = static_cast<const float*>(d_data);
    int block_size = 256;
    int grid_size = (dataset.n + block_size - 1) / block_size;

    for (int i = 0; i < dataset.n; ++i) {
        compute_distances_kernel<<<grid_size, block_size>>>(data_ptr, dataset.n, dataset.dim, i, d_distances, d_indices);
        CUDA_CHECK(cudaGetLastError());
        CUDA_CHECK(cudaDeviceSynchronize());

        sort_by_distance(d_distances, d_indices, dataset.n);
        CUDA_CHECK(cudaMemcpy(h_distances, d_distances, k * sizeof(float), cudaMemcpyDeviceToHost));
        CUDA_CHECK(cudaMemcpy(h_indices, d_indices, k * sizeof(int), cudaMemcpyDeviceToHost));

        for (int j = 0; j < k; ++j) {
            graph.neighbors[i][j] = h_indices[j];
        }

        if (debug && i % 1000 == 0) {
            std::cout << "Built neighbors for point " << i << std::endl;
        }
    }

    delete[] h_distances;
    delete[] h_indices;
    CUDA_CHECK(cudaFree(d_distances));
    CUDA_CHECK(cudaFree(d_indices));

    if (verbose) {
        std::cout << "KNN graph built with k=" << k << std::endl;
    }
}

