void KNNWrapper::generate_ground_truth() {
    ground_truth.n = dataset.n;
    ground_truth.k = k;
    ground_truth.neighbors.resize(dataset.n, std::vector<int>(k));

    for (int i = 0; i < dataset.n; ++i) {
        std::vector<std::pair<float, int>> dists;
        for (int j = 0; j < dataset.n; ++j) {
            float dist = euclidean_distance(dataset.data[i], dataset.data[j]);
            dists.emplace_back(dist, j);
        }
        std::sort(dists.begin(), dists.end());
