void KNNWrapper::evaluate_graph() {
    int match_count = 0;
    int total = 0;
    for (int i = 0; i < dataset.n; ++i) {
        const auto& pred = graph.neighbors[i];
        const auto& true_nn = ground_truth.neighbors[i];
        for (int p : pred) {
            if (std::find(true_nn.begin(), true_nn.end(), p) != true_nn.end()) {
                ++match_count;
            }
        }
        total += k;
    }
    float recall = static_cast<float>(match_count) / total;
    std::cout << "Recall@k: " << recall << " (" << match_count << "/" << total << ")" << std::endl;
}

