### 🛠 Реализация `TransportBuilder`

```rust
use elasticsearch::{http::transport::Transport, http::Url};
use reqwest::{
    Client, ClientBuilder,
    tls::{Certificate, Version},
};
use std::time::Duration;
use std::collections::HashMap;

pub struct TransportBuilder {
    url: Url,
    proxy: Option<String>,
    proxy_auth: Option<(String, String)>,
    disable_proxy: bool,
    client_cert: Option<Certificate>,
    headers: HashMap<String, String>,
    auth: Option<Auth>,
    timeout: Option<Duration>,
}

enum Auth {
    Basic(String, String),
    Bearer(String),
    // Можно расширить при необходимости
}

impl TransportBuilder {
    pub fn new(url: Url) -> Self {
        Self {
            url,
            proxy: None,
            proxy_auth: None,
            disable_proxy: false,
            client_cert: None,
            headers: HashMap::new(),
            auth: None,
            timeout: None,
        }
    }

    pub fn proxy(mut self, url: impl Into<String>) -> Self {
        self.proxy = Some(url.into());
        self
    }

    pub fn proxy_auth(mut self, username: impl Into<String>, password: impl Into<String>) -> Self {
        self.proxy_auth = Some((username.into(), password.into()));
        self
    }

    pub fn no_proxy(mut self) -> Self {
        self.disable_proxy = true;
        self
    }

    pub fn client_cert(mut self, cert: Certificate) -> Self {
        self.client_cert = Some(cert);
        self
    }

    pub fn header(mut self, name: impl Into<String>, value: impl Into<String>) -> Self {
        self.headers.insert(name.into(), value.into());
        self
    }

    pub fn auth_basic(mut self, username: impl Into<String>, password: impl Into<String>) -> Self {
        self.auth = Some(Auth::Basic(username.into(), password.into()));
        self
    }

    pub fn auth_bearer(mut self, token: impl Into<String>) -> Self {
        self.auth = Some(Auth::Bearer(token.into()));
        self
    }

    pub fn timeout(mut self, timeout: Duration) -> Self {
        self.timeout = Some(timeout);
        self
    }

    pub fn build(self) -> Result<Transport, Box<dyn std::error::Error>> {
        let mut client_builder = ClientBuilder::new();

        // Настройка прокси
        if self.disable_proxy {
            client_builder = client_builder.no_proxy();
        } else if let Some(proxy_url) = &self.proxy {
            let mut proxy = reqwest::Proxy::all(proxy_url)?;

            if let Some((username, password)) = &self.proxy_auth {
                proxy = proxy.basic_auth(username, password);
            }

            client_builder = client_builder.proxy(proxy);
        }

        // Установка таймаута
        if let Some(timeout) = self.timeout {
            client_builder = client_builder.timeout(timeout);
        }

        // Установка корневого сертификата (если задан)
        if let Some(cert) = self.client_cert {
            client_builder = client_builder.add_root_certificate(cert);
        }

        // Установка заголовков по умолчанию
        let mut headers = reqwest::header::HeaderMap::new();
        for (name, value) in self.headers {
            let name = reqwest::header::HeaderName::from_bytes(name.as_bytes())?;
            let value = reqwest::header::HeaderValue::from_str(&value)?;
            headers.insert(name, value);
        }

        // Установка аутентификации (если требуется)
        if let Some(auth) = self.auth {
            let mut auth_value = String::new();
            match auth {
                Auth::Basic(username, password) => {
                    let user_pass = format!("{}:{}", username, password);
                    let encoded = base64::encode(user_pass);
                    auth_value = format!("Basic {}", encoded);
                }
                Auth::Bearer(token) => {
                    auth_value = format!("Bearer {}", token);
                }
            }
            let mut auth_header_value = reqwest::header::HeaderValue::from_str(&auth_value)?;
            auth_header_value.set_sensitive(true);
            headers.insert(reqwest::header::AUTHORIZATION, auth_header_value);
        }

        client_builder = client_builder.default_headers(headers);

        // Сборка клиента
        let client = client_builder.build()?;

        // Создание транспорта Elasticsearch
        let transport = Transport::single_node(&self.url, Some(client))?;

        Ok(transport)
    }
}
```

