// build/build.js
const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

const srcDir = path.join(__dirname, '../src');
const publicDir = path.join(__dirname, '../public');

// Утилита: создать каталог, если его нет
function ensureDir(dir) {
  if (!fs.existsSync(dir)) {
    fs.mkdirSync(dir, { recursive: true });
    console.log(`✅ Создан каталог: ${dir}`);
  }
}

// Утилита: скопировать файл
function copyFile(src, dest) {
  fs.copyFileSync(src, dest);
  console.log(`📋 Скопирован: ${src} → ${dest}`);
}

// Утилита: записать файл
function writeFile(dest, content) {
  fs.writeFileSync(dest, content);
  console.log(`📝 Записан: ${dest}`);
}

// Основная функция сборки
function build() {
  console.log('🚀 Запуск сборки...\n');

  // 1. Создание публичного каталога и подкаталогов
  ensureDir(publicDir);
  const subdirs = ['js', 'css', 'img', 'fonts'];
  subdirs.forEach(dir => ensureDir(path.join(publicDir, dir)));

  // 2. Компиляция EJS шаблона в HTML
  const templatePath = path.join(srcDir, 'templates', 'index.ejs');
  const indexPath = path.join(publicDir, 'index.html');
  if (fs.existsSync(templatePath)) {
    const template = fs.readFileSync(templatePath, 'utf8');
    const compiledHtml = template
      .replace(/<%= title %>/g, 'Мой Веб-Проект')
      .replace(/<%= content %>/g, 'Добро пожаловать!');
    writeFile(indexPath, compiledHtml);
  } else {
    console.warn(`⚠️  Шаблон не найден: ${templatePath}`);
  }

  // 3. Генерация парсера из grammar.ne с помощью nearleyc
  const grammarSrc = path.join(srcDir, 'parsers', 'grammar.ne');
  const parserDest = path.join(srcDir, 'parsers', 'generated_parser.js');
  if (fs.existsSync(grammarSrc)) {
    try {
      execSync(`npx nearleyc ${grammarSrc} -o ${parserDest}`);
      console.log(`🧩 Сгенерирован парсер: ${parserDest}`);
    } catch (err) {
      console.error(`❌ Ошибка при генерации парсера:`, err.stderr.toString());
    }
  } else {
    console.warn(`⚠️  Грамматика не найдена: ${grammarSrc}`);
  }

  // 4. Компиляция SCSS в CSS
  const scssSrc = path.join(srcDir, 'scss', 'styles.scss');
  const cssDest = path.join(publicDir, 'css', 'styles.css');
  if (fs.existsSync(scssSrc)) {
    try {
      execSync(`npx sass ${scssSrc} ${cssDest}`);
      console.log(`🎨 Скомпилирован SCSS: ${cssDest}`);
    } catch (err) {
      console.error(`❌ Ошибка компиляции SCSS:`, err.stderr.toString());
    }
  } else {
    console.warn(`⚠️  SCSS файл не найден: ${scssSrc}`);
  }

  // 5. Копирование JS
  const jsSrc = path.join(srcDir, 'js', 'app.js');
  const jsDest = path.join(publicDir, 'js', 'app.js');
  if (fs.existsSync(jsSrc)) {
    copyFile(jsSrc, jsDest);
  } else {
    console.warn(`⚠️  JS файл не найден: ${jsSrc}`);
  }

  // 6. Копирование изображений (пример)
  const imgSrcDir = path.join(srcDir, 'img');
  const imgDestDir = path.join(publicDir, 'img');
  if (fs.existsSync(imgSrcDir)) {
    fs.readdirSync(imgSrcDir).forEach(file => {
      copyFile(path.join(imgSrcDir, file), path.join(imgDestDir, file));
    });
  }

  console.log('\n🎉 Сборка завершена! Публичные файлы доступны в /public');
}

// Запуск
build();
