## ✅ 2. Ядро CUDA: Обновление состояния клеток

```cpp
#include <cuda_runtime.h>
#include <stdio.h>
#include <vector>
#include <memory>
#include <fstream>
#include <iostream>
#include <chrono>

// Assuming MAPS-Multi provides these
extern "C" void maps_init(int* argc, char*** argv);
extern "C" int maps_get_rank();
extern "C" int maps_get_size();
extern "C" void maps_barrier();
extern "C" void maps_exchange_halos(unsigned char* grid, int width, int height);
extern "C" void maps_save_image(const unsigned char* grid, int width, int height, const char* filename);

// Настройки
#define BLOCK_SIZE 16
#define MAX_ITERATIONS 1000
#define TEST_EXPECTED_FILE "expected_grid.bin"

// CUDA kernel
__global__ void game_of_life_kernel(const unsigned char* old_grid,
                                    unsigned char* new_grid,
                                    int width,
                                    int height) {
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    int idx = y * width + x;

    // Пропускаем границы (halo zones), обрабатываем только внутреннюю часть
    if (x == 0 || x == width - 1 || y == 0 || y == height - 1) {
        new_grid[idx] = old_grid[idx];
        return;
    }

    // Подсчёт живых соседей
    int live_neighbors = 0;
    for (int dy = -1; dy <= 1; dy++) {
        for (int dx = -1; dx <= 1; dx++) {
            if (dx == 0 && dy == 0) continue;
            int nx = x + dx;
            int ny = y + dy;
            live_neighbors += old_grid[ny * width + nx];
        }
    }

    // Применение правил
    if (old_grid[idx] == 1) {
        new_grid[idx] = (live_neighbors == 2 || live_neighbors == 3) ? 1 : 0;
    } else {
        new_grid[idx] = (live_neighbors == 3) ? 1 : 0;
    }
}
