## ✅ 3. Управление несколькими GPU: Разделение сетки

```cpp
struct DeviceData {
    unsigned char *d_old;
    unsigned char *d_new;
    unsigned char *h_grid;
    int width, height;
    int device_id;
};

// Инициализация данных на GPU
void init_device_data(std::vector<DeviceData>& devices, int global_width, int total_height) {
    int num_devices = devices.size();
    int base_height_per_gpu = total_height / num_devices;
    int remainder = total_height % num_devices;

    size_t offset = 0;
    for (int i = 0; i < num_devices; ++i) {
        int local_height = base_height_per_gpu + (i < remainder ? 1 : 0);
        int padded_height = local_height + 2;  // верхняя и нижняя границы (halo)

        devices[i].width = global_width;
        devices[i].height = padded_height;
        devices[i].device_id = i;

        // Выделение памяти на GPU
        cudaSetDevice(i);
        cudaMalloc(&devices[i].d_old, global_width * padded_height * sizeof(unsigned char));
        cudaMalloc(&devices[i].d_new, global_width * padded_height * sizeof(unsigned char));
        devices[i].h_grid = new unsigned char[global_width * padded_height];

        // Инициализация CPU-буфера (с учетом смещения)
        for (int y = 0; y < padded_height; ++y) {
            for (int x = 0; x < global_width; ++x) {
                int src_y = (y - 1) + offset;  // -1 из-за halo
                if (y == 0 || y == padded_height - 1) {
                    devices[i].h_grid[y * global_width + x] = 0;  // границы = 0
                } else if (src_y >= 0 && src_y < total_height) {
                    devices[i].h_grid[y * global_width + x] = (rand() % 100) < 30 ? 1 : 0;  // 30% шанс живой
                } else {
                    devices[i].h_grid[y * global_width + x] = 0;
                }
            }
        }

        cudaMemcpy(devices[i].d_old, devices[i].h_grid, global_width * padded_height, cudaMemcpyHostToDevice);

        offset += local_height;
    }
}
