## ✅ 4. Основной цикл симуляции

```cpp
void run_simulation(std::vector<DeviceData>& devices, int iterations) {
    dim3 blockSize(BLOCK_SIZE, BLOCK_SIZE);
    for (int iter = 0; iter < iterations; ++iter) {
        for (int i = 0; i < devices.size(); ++i) {
            cudaSetDevice(i);
            dim3 gridSize((devices[i].width + blockSize.x - 1) / blockSize.x,
                          (devices[i].height + blockSize.y - 1) / blockSize.y);

            // Обмен граничными данными между GPU
            if (devices.size() > 1) {
                maps_exchange_halos(devices[i].d_old, devices[i].width, devices[i].height);
            }

            // Запуск ядра
            game_of_life_kernel<<<gridSize, blockSize>>>(
                devices[i].d_old, devices[i].d_new, devices[i].width, devices[i].height);

            // Переключение буферов
            std::swap(devices[i].d_old, devices[i].d_new);
        }

        // Синхронизация между GPU
        maps_barrier();
    }
}
