## ✅ 5. Регрессионные тесты и сохранение изображений

```cpp
bool compare_with_expected(const std::vector<DeviceData>& devices, const char* expected_file) {
    std::vector<unsigned char> expected;
    std::ifstream file(expected_file, std::ios::binary);
    if (!file) return false;

    file.seekg(0, std::ios::end);
    size_t size = file.tellg();
    file.seekg(0, std::ios::beg);
    expected.resize(size);
    file.read(reinterpret_cast<char*>(expected.data()), size);

    // Сбор данных с GPU
    std::vector<unsigned char> actual;
    for (const auto& d : devices) {
        std::vector<unsigned char> local(d.width * (d.height - 2)); // без границ
        cudaSetDevice(d.device_id);
        cudaMemcpy(d.h_grid, d.d_old, d.width * d.height * sizeof(unsigned char), cudaMemcpyDeviceToHost);
        for (int y = 1; y < d.height - 1; ++y) {
            std::copy(d.h_grid + y * d.width,
                      d.h_grid + y * d.width + d.width,
                      local.begin() + (y - 1) * d.width);
        }
        actual.insert(actual.end(), local.begin(), local.end());
    }

    return actual == expected;
}

void save_current_state(const std::vector<DeviceData>& devices, const char* filename) {
    std::vector<unsigned char> image;
    for (const auto& d : devices) {
        std::vector<unsigned char> local(d.width * (d.height - 2));
        cudaSetDevice(d.device_id);
        cudaMemcpy(d.h_grid, d.d_old, d.width * d.height * sizeof(unsigned char), cudaMemcpyDeviceToHost);
        for (int y = 1; y < d.height - 1; ++y) {
            std::copy(d.h_grid + y * d.width,
                      d.h_grid + y * d.width + d.width,
                      local.begin() + (y - 1) * d.width);
        }
        image.insert(image.end(), local.begin(), local.end());
    }

    maps_save_image(image.data(), devices[0].width, image.size() / devices[0].width, filename);
}
