## ✅ 6. Основная функция

```cpp
int main(int argc, char** argv) {
    maps_init(&argc, &argv);
    int rank = maps_get_rank();

    // Инициализация CUDA
    int num_gpus;
    cudaGetDeviceCount(&num_gpus);
    std::vector<DeviceData> devices(num_gpus);

    // Настройка симуляции
    int global_width = 1024;
    int global_height = 1024;
    int iterations = 100;

    if (rank == 0) {
        printf("Запуск Game of Life на %d GPU, сетка %dx%d, %d итераций\n",
               num_gpus, global_width, global_height, iterations);
    }

    init_device_data(devices, global_width, global_height);
    auto start = std::chrono::high_resolution_clock::now();
    run_simulation(devices, iterations);
    auto end = std::chrono::high_resolution_clock::now();

    if (rank == 0) {
        auto duration = std::chrono::duration_cast<std::chrono::milliseconds>(end - start);
        printf("Симуляция завершена за %ld мс\n", duration.count());
    }

    // Регрессионный тест
    if (!compare_with_expected(devices, TEST_EXPECTED_FILE)) {
        if (rank == 0) {
            printf("[ОШИБКА] Регрессионный тест не прошёл. Сохранение изображения...\n");
            save_current_state(devices, "failed_test_output.png");
        }
    } else {
        if (rank == 0) {
            printf("Регрессионный тест пройден.\n");
        }
    }

    // Очистка
    for (auto& d : devices) {
        cudaSetDevice(d.device_id);
        cudaFree(d.d_old);
        cudaFree(d.d_new);
        delete[] d.h_grid;
    }

    return 0;
}
