#include "knn_graph.h"
#include "utils.h"
#include <cuda_runtime.h>
#include <thrust/device_vector.h>
#include <thrust/sort.h>
#include <thrust/sequence.h>
#include <iostream>
#include <chrono>

KNNGraph::KNNGraph(const KNNParams& params) : params_(params), d_data_(nullptr), d_queries_(nullptr),
                                            d_graph_(nullptr), d_indices_(nullptr), d_distances_(nullptr),
                                            n_(0), dim_(0) {}

KNNGraph::~KNNGraph() {
    clear();
}

void KNNGraph::log(const std::string& msg) {
    if (params_.verbose) {
        std::cout << "[KNN] " << msg << std::endl;
    }
}

bool KNNGraph::load_data(const std::string& filename, int dim) {
    dim_ = dim;
    std::ifstream file(filename, std::ios::binary);
    if (!file) {
        std::cerr << "Cannot open data file: " << filename << std::endl;
        return false;
    }

    file.seekg(0, std::ios::end);
    size_t file_size = file.tellg();
    file.seekg(0, std::ios::beg);

    n_ = file_size / (dim * sizeof(float));
    data_.resize(n_ * dim_);

    file.read(reinterpret_cast<char*>(data_.data()), n_ * dim_ * sizeof(float));
    file.close();

    log("Loaded " + std::to_string(n_) + " vectors of dim " + std::to_string(dim_));

    return allocate_gpu_memory();
}

bool KNNGraph::allocate_gpu_memory() {
    cudaError_t err;

    err = cudaMalloc(&d_data_, n_ * dim_ * sizeof(float));
    if (err != cudaSuccess) {
        std::cerr << "CUDA malloc failed: " << cudaGetErrorString(err) << std::endl;
        return false;
    }

    err = cudaMemcpy(d_data_, data_.data(), n_ * dim_ * sizeof(float), cudaMemcpyHostToDevice);
    if (err != cudaSuccess) {
        std::cerr << "CUDA memcpy failed: " << cudaGetErrorString(err) << std::endl;
        return false;
    }

    // Выделение памяти под граф (n * k)
    graph_.resize(n_, std::vector<int>(params_.k));
    size_t graph_bytes = n_ * params_.k * sizeof(int);
    err = cudaMalloc(&d_graph_, graph_bytes);
    if (err != cudaSuccess) return false;

    // Выделение временных массивов для сортировки
    d_indices_ = thrust::raw_pointer_cast(
        new thrust::device_vector<int>(params_.k * n_).data());
    d_distances_ = thrust::raw_pointer_cast(
        new thrust::device_vector<float>(params_.k * n_).data());

    return true;
}

void KNNGraph::free_gpu_memory() {
    if (d_data_) cudaFree(d_data_);
    if (d_queries_) cudaFree(d_queries_);
    if (d_graph_) cudaFree(d_graph_);
    if (d_indices_) cudaFree(d_indices_);
    if (d_distances_) cudaFree(d_distances_);

    d_data_ = d_queries_ = d_graph_ = d_indices_ = d_distances_ = nullptr;
}

bool KNNGraph::build_graph() {
    log("Building KNN graph (k=" + std::to_string(params_.k) + ")...");

    // Простой пример: brute-force на GPU (для демонстрации)
    // В реальности — используйте FAISS, cuML, или оптимизированные ядра

    const int block_size = 256;
    const int grid_size = (n_ + block_size - 1) / block_size;

    // Временный массив для результатов (на GPU)
    thrust::device_vector<int> indices_dev(n_ * params_.k);
    thrust::device_vector<float> dists_dev(n_ * params_.k);

    // Запуск ядра для каждой точки
    // (в реальности используйте блочный или приближённый метод)
    build_knn_brute_force<<<grid_size, block_size>>>(
        d_data_, n_, dim_, params_.k, thrust::raw_pointer_cast(indices_dev.data()),
        thrust::raw_pointer_cast(dists_dev.data())
    );

    cudaDeviceSynchronize();
    if (cudaGetLastError() != cudaSuccess) {
        std::cerr << "CUDA kernel failed" << std::endl;
        return false;
    }

    // Копируем результаты обратно
    thrust::host_vector<int> h_indices = indices_dev;
    for (int i = 0; i < n_; ++i) {
        for (int j = 0; j < params_.k; ++j) {
            graph_[i][j] = h_indices[i * params_.k + j];
        }
    }

    log("KNN graph built.");
    return true;
}

