bool KNNGraph::generate_ground_truth() {
    log("Generating ground truth (brute-force CPU)...");

    // Пример: использовать первые `num_queries` как запросы
    int num_queries = std::min(params_.num_queries, n_);
    queries_.resize(num_queries * dim_);
    for (int i = 0; i < num_queries; ++i) {
        std::copy(data_.begin() + i * dim_, data_.begin() + (i + 1) * dim_, queries_.begin() + i * dim_);
    }

    ground_truth_.resize(num_queries, std::vector<int>(params_.k));
    for (int q = 0; q < num_queries; ++q) {
        std::vector<std::pair<float, int>> distances;
        for (int i = 0; i < n_; ++i) {
            float dist = l2_distance(&queries_[q * dim_], &data_[i * dim_], dim_);
            distances.emplace_back(dist, i);
        }
        std::sort(distances.begin(), distances.end());
        for (int j = 0; j < params_.k; ++j) {
            ground_truth_[q][j] = distances[j].second;
        }
    }

    log("Ground truth generated for " + std::to_string(num_queries) + " queries.");
    return true;
}

double KNNGraph::evaluate() {
    if (ground_truth_.empty()) {
        std::cerr << "No ground truth available for evaluation." << std::endl;
        return 0.0;
    }

    int num_queries = ground_truth_.size();
    int matches = 0;
    int total = num_queries * params_.k;

    for (int q = 0; q < num_queries; ++q) {
        const auto& true_neighbors = ground_truth_[q];
        const auto& found_neighbors = graph_[q]; // Предполагаем, что мы ищем по индексу q

        for (int j = 0; j < params_.k; ++j) {
            int found = found_neighbors[j];
            if (std::find(true_neighbors.begin(), true_neighbors.end(), found) != true_neighbors.end()) {
                matches++;
            }
        }
    }

    double recall = static_cast<double>(matches) / total;
    log("Recall@k: " + std::to_string(recall));
    return recall;
}

std::vector<std::vector<int>> KNNGraph::query(const std::vector<float>& query_vectors, int num_queries) {
    std::vector<std::vector<int>> results(num_queries, std::vector<int>(params_.k));
    log("Running " + std::to_string(num_queries) + " queries...");

    // Пример: используем граф для graph-based search (например, HNSW-style)
    // Простой пример: стартуем с случайной точки и идём по графу
    for (int q_idx = 0; q_idx < num_queries; ++q_idx) {
        const float* q = query_vectors.data() + q_idx * dim_;
        std::vector<int> candidates = { rand() % n_ }; // начальная точка
        std::vector<std::pair<float, int>> best(params_.k + 1);

        for (int step = 0; step < 100; ++step) { // макс. шагов
            std::vector<int> new_candidates;
            for (int v : candidates) {
                // Оценим всех соседей
                for (int neighbor : graph_[v]) {
                    float dist = l2_distance(q, &data_[neighbor * dim_], dim_);
                    best.push_back({dist, neighbor});
                }
            }
            // Сортируем и оставляем лучшие
            std::sort(best.begin(), best.end());
            best.resize(params_.k);
            // Обновляем кандидатов
            new_candidates.clear();
            for (auto& p : best) new_candidates.push_back(p.second);
            if (new_candidates == candidates) break;
            candidates = new_candidates;
        }

        for (int j = 0; j < params_.k && j < best.size(); ++j) {
            results[q_idx][j] = best[j].second;
        }
    }

    return results;
}

bool KNNGraph::save_graph(const std::string& filename) {
    std::ofstream file(filename, std::ios::binary);
    if (!file) return false;

    file.write(reinterpret_cast<const char*>(&n_), sizeof(int));
    file.write(reinterpret_cast<const char*>(&params_.k), sizeof(int));
    for (int i = 0; i < n_; ++i) {
        file.write(reinterpret_cast<const char*>(graph_[i].data()), params_.k * sizeof(int));
    }
    file.close();
    log("Graph saved to " + filename);
    return true;
}

bool KNNGraph::load_graph(const std::string& filename) {
    std::ifstream file(filename, std::ios::binary);
    if (!file) return false;

    int k;
    file.read(reinterpret_cast<char*>(&n_), sizeof(int));
    file.read(reinterpret_cast<char*>(&k), sizeof(int));
    if (k != params_.k) {
        std::cerr << "Mismatched k value in graph file." << std::endl;
        return false;
    }

    graph_.resize(n_, std::vector<int>(params_.k));
    for (int i = 0; i < n_; ++i) {
        file.read(reinterpret_cast<char*>(graph_[i].data()), params_.k * sizeof(int));
    }
    file.close();

    log("Graph loaded from " + filename);
    return true;
}

bool KNNGraph::reset_for_subset(int subset_size) {
    if (subset_size > n_) return false;

    log("Resetting graph for subset of size " + std::to_string(subset_size));

    n_ = subset_size;
    data_.resize(n_ * dim_);

    graph_.clear();
    graph_.resize(n_, std::vector<int>(params_.k));

    free_gpu_memory();
    return allocate_gpu_memory();
}

void KNNGraph::clear() {
    free_gpu_memory();
    data_.clear();
    queries_.clear();
    graph_.clear();
    ground_truth_.clear();
}
