```rust
use reqwest::{
    Client,
    ClientBuilder,
    Proxy,
    header::{HeaderMap, HeaderValue},
};
use std::time::Duration;
use std::path::Path;
use native_tls::{Certificate, TlsConnector};
use reqwest::tls::TlsBuilder;

/// Учётные данные для аутентификации в Elasticsearch
#[derive(Clone)]
pub enum Auth {
    Basic(String, String), // (username, password)
    Bearer(String),        // token
}

/// Строитель для создания HTTP-транспорта к Elasticsearch
pub struct TransportBuilder {
    proxy_url: Option<String>,
    proxy_username: Option<String>,
    proxy_password: Option<String>,
    disable_proxy: bool,
    auth: Option<Auth>,
    ca_cert_path: Option<String>,
    headers: HeaderMap,
    timeout: Option<Duration>,
}

impl TransportBuilder {
    pub fn new() -> Self {
        Self {
            proxy_url: None,
            proxy_username: None,
            proxy_password: None,
            disable_proxy: false,
            auth: None,
            ca_cert_path: None,
            headers: HeaderMap::new(),
            timeout: None,
        }
    }

    /// Установка прокси с опциональной базовой аутентификацией
    pub fn proxy(mut self, url: &str, username: Option<&str>, password: Option<&str>) -> Self {
        self.proxy_url = Some(url.to_string());
        self.proxy_username = username.map(|s| s.to_string());
        self.proxy_password = password.map(|s| s.to_string());
        self
    }

    /// Отключение всех прокси (включая системные)
    pub fn no_proxy(mut self) -> Self {
        self.disable_proxy = true;
        self
    }

    /// Установка учётных данных аутентификации
    pub fn auth(mut self, auth: Auth) -> Self {
        self.auth = Some(auth);
        self
    }

    /// Установка пути к CA-сертификату для проверки HTTPS
    pub fn certificate(mut self, cert_path: &str) -> Self {
        self.ca_cert_path = Some(cert_path.to_string());
        self
    }

    /// Добавление глобального HTTP-заголовка
    pub fn header<K, V>(mut self, key: K, value: V) -> Result<Self, Box<dyn std::error::Error>>
    where
        K: TryInto<reqwest::header::HeaderName>,
        K::Error: std::error::Error + 'static,
        V: TryInto<reqwest::header::HeaderValue>,
        V::Error: std::error::Error + 'static,
    {
        let key = key.try_into().map_err(|e| format!("Invalid header name: {}", e))?;
        let value = value.try_into().map_err(|e| format!("Invalid header value: {}", e))?;
        self.headers.insert(key, value);
        Ok(self)
    }

    /// Установка глобального тайм-аута
    pub fn timeout(mut self, duration: Duration) -> Self {
        self.timeout = Some(duration);
        self
    }

    /// Построение итогового HTTP-транспорта (reqwest::Client)
    pub fn build(self) -> Result<Client, Box<dyn std::error::Error>> {
        let mut client_builder = ClientBuilder::new();

        // Настройка прокси
        if !self.disable_proxy {
            if let Some(proxy_url) = &self.proxy_url {
                let mut proxy = Proxy::all(proxy_url)?;

                // Добавление базовой аутентификации для прокси при наличии
                if let (Some(username), Some(password)) = (&self.proxy_username, &self.proxy_password) {
                    proxy = proxy.basic_auth(username, password);
                }

                client_builder = client_builder.proxy(proxy);
            }
        } else {
            client_builder = client_builder.no_proxy();
        }

        // Настройка TLS и проверки сертификата
        if let Some(cert_path) = &self.ca_cert_path {
            let cert_data = std::fs::read(cert_path)?;
            let cert = Certificate::from_pem(&cert_data)?;
            let mut tls_builder = TlsConnector::builder();
            tls_builder.add_root_certificate(cert);
            let tls = tls_builder.build()?;
            client_builder = client_builder.tls_connector(tls.into());
        }

        // Установка заголовков
        client_builder = client_builder.default_headers(self.headers);

        // Установка аутентификации
        if let Some(auth) = self.auth {
            match auth {
                Auth::Basic(username, password) => {
                    client_builder = client_builder.basic_auth(&username, Some(&password));
                }
                Auth::Bearer(token) => {
                    client_builder = client_builder.bearer_auth(&token);
                }
            }
        }

        // Установка тайм-аута
        if let Some(timeout) = self.timeout {
            client_builder = client_builder.timeout(timeout);
        }

        // Сборка клиента
        let client = client_builder.build()?;
        Ok(client)
    }
}

