// build.js
const fs = require('fs');
const path = require('path');
const peg = require('pegjs'); // Для генерации парсеров
const ejs = require('ejs');  // Для рендеринга шаблонов
const { execSync } = require('child_process');

const SRC_DIR = path.join(__dirname, 'src');
const PUBLIC_DIR = path.join(__dirname, 'public');
const DIST_DIR = path.join(PUBLIC_DIR, 'dist');
const TEMPLATES_DIR = path.join(SRC_DIR, 'templates');
const PARSERS_DIR = path.join(SRC_DIR, 'parsers');
const ASSETS_DIR = path.join(SRC_DIR, 'assets');

// Утилита: гарантирует существование директории
function ensureDir(dir) {
  if (!fs.existsSync(dir)) {
    fs.mkdirSync(dir, { recursive: true });
  }
}

// 1. Генерация парсеров из .pegjs файлов
function generateParsers() {
  console.log('📦 Генерация парсеров...');
  const parserFiles = fs.readdirSync(PARSERS_DIR).filter(f => f.endsWith('.pegjs'));
  ensureDir(DIST_DIR);

  parserFiles.forEach(file => {
    const inputPath = path.join(PARSERS_DIR, file);
    const outputPath = path.join(DIST_DIR, file.replace('.pegjs', '.parser.js'));
    const grammar = fs.readFileSync(inputPath, 'utf8');

    try {
      const parserCode = peg.generate(grammar);
      fs.writeFileSync(outputPath, parserCode.toString());
      console.log(`✅ Парсер сгенерирован: ${outputPath}`);
    } catch (err) {
      console.error(`❌ Ошибка при генерации парсера ${file}:`, err.message);
    }
  });
}

// 2. Рендеринг шаблонов (например, EJS → HTML)
function renderTemplates() {
  console.log('🎨 Рендеринг шаблонов...');
  const templateFiles = fs.readdirSync(TEMPLATES_DIR).filter(f => f.endsWith('.ejs'));
  const data = JSON.parse(fs.readFileSync(path.join(__dirname, 'data.json'), 'utf8'));

  templateFiles.forEach(file => {
    const templatePath = path.join(TEMPLATES_DIR, file);
    const htmlPath = path.join(PUBLIC_DIR, file.replace('.ejs', '.html'));

    const template = fs.readFileSync(templatePath, 'utf8');
    const html = ejs.render(template, data);

    fs.writeFileSync(htmlPath, html);
    console.log(`✅ Шаблон обработан: ${htmlPath}`);
  });
}

// 3. Копирование статических ресурсов (CSS, JS, изображения)
function copyAssets() {
  console.log('📁 Копирование статики...');
  const assets = ['css', 'js', 'img'];

  assets.forEach(asset => {
    const src = path.join(ASSETS_DIR, asset);
    const dest = path.join(PUBLIC_DIR, asset);

    if (fs.existsSync(src)) {
      execSync(`cp -r "${src}" "${dest}"`, { stdio: 'inherit' });
      console.log(`✅ Скопировано: ${asset}/`);
    }
  });
}

// 4. Создание структуры public каталога
function createPublicStructure() {
  console.log('🏗️ Создание структуры public...');
  ensureDir(PUBLIC_DIR);
  ensureDir(path.join(PUBLIC_DIR, 'css'));
  ensureDir(path.join(PUBLIC_DIR, 'js'));
  ensureDir(path.join(PUBLIC_DIR, 'img'));
