void save_grid_to_ppm(const std::vector<unsigned char>& grid, int width, int height, const std::string& filename) {
    std::ofstream file(filename, std::ios::binary);
    if (!file) {
        std::cerr << "Cannot open file: " << filename << std::endl;
        return;
    }

    file << "P6\n" << width << " " << height << "\n255\n";

    for (int i = 0; i < height; ++i) {
        for (int j = 0; j < width; ++j) {
            unsigned char val = grid[i * width + j] ? 255 : 0;
            unsigned char rgb[3] = {val, val, val}; // grayscale
            file.write(reinterpret_cast<char*>(rgb), 3);
        }
    }
    file.close();
    std::cout << "Saved: " << filename << std::endl;
}
