### 3. **Интеграция с MAPS-Multi — Основная симуляция**

```cpp
// main_simulation.cpp
#include <vector>
#include <chrono>
#include "maps_multi.hpp"  // гипотетический хедер
#include "image_io.cpp"
#include "game_of_life_kernel.cu"

// Настройки
const int TOTAL_ITERATIONS = 100;
const int BLOCK_SIZE = 16;  // 16x16 блоки

struct GridConfig {
    unsigned char* d_current;
    unsigned char* d_next;
    size_t pitch;
    int width, height;
};

// Инициализация сетки (например, "Glider")
void initialize_grid(std::vector<unsigned char>& grid, int width, int height) {
    std::fill(grid.begin(), grid.end(), 0);
    int cx = width / 2;
    int cy = height / 2;
    grid[cy * width + cx] = 1;
    grid[cy * width + cx + 1] = 1;
    grid[cy * width + cx + 2] = 1;
    grid[(cy - 1) * width + cx + 2] = 1;
    grid[(cy - 2) * width + cx + 1] = 1;
}

// Проверка регрессии
bool compare_grids(const std::vector<unsigned char>& a, const std::vector<unsigned char>& b) {
    return a == b;
}

// Основная симуляция
int main() {
    maps::init();

    int num_devices = maps::get_num_devices();
    int device_id = maps::get_device_id();

    // Разделим глобальную сетку на части по Y
    const int GLOBAL_WIDTH = 512;
    const int GLOBAL_HEIGHT = 512;
    const int LOCAL_HEIGHT = GLOBAL_HEIGHT / num_devices;
    const int LOCAL_WIDTH = GLOBAL_WIDTH;

    GridConfig grid;
    grid.width = LOCAL_WIDTH;
    grid.height = LOCAL_HEIGHT;

    // Выделение памяти на GPU
    maps::allocate(&grid.d_current, LOCAL_WIDTH * LOCAL_HEIGHT * sizeof(unsigned char));
    maps::allocate(&grid.d_next, LOCAL_WIDTH * LOCAL_HEIGHT * sizeof(unsigned char));

    // Выделение памяти на CPU
    std::vector<unsigned char> h_current_full(GLOBAL_WIDTH * GLOBAL_HEIGHT, 0);
    std::vector<unsigned char> h_current_local(LOCAL_WIDTH * LOCAL_HEIGHT);
    std::vector<unsigned char> h_next_local(LOCAL_WIDTH * LOCAL_HEIGHT);

    // Инициализация начального состояния на CPU (на главном GPU)
    if (device_id == 0) {
        initialize_grid(h_current_full, GLOBAL_WIDTH, GLOBAL_HEIGHT);
    }

    // Рассылаем данные по GPU (scatter)
    maps::scatter(h_current_full.data(), h_current_local.data(),
                  GLOBAL_WIDTH, GLOBAL_HEIGHT, device_id, num_devices);

    // Копируем на GPU
    maps::memcpy(grid.d_current, h_current_local.data(),
                 LOCAL_WIDTH * LOCAL_HEIGHT * sizeof(unsigned char), maps::HostToDevice);

    // Настройка kernel launch
    dim3 blockSize(BLOCK_SIZE, BLOCK_SIZE);
    dim3 gridSize(
        (LOCAL_WIDTH + blockSize.x - 1) / blockSize.x,
        (LOCAL_HEIGHT + blockSize.y - 1) / blockSize.y
    );

    // Основной цикл симуляции
    for (int step = 0; step < TOTAL_ITERATIONS; ++step) {
        // Обмен границами (ghost cells)
        maps::exchange_ghosts(grid.d_current, LOCAL_WIDTH, LOCAL_HEIGHT, sizeof(unsigned char));

        // Запуск ядра
        maps::launch_kernel(
            game_of_life_kernel,
            gridSize, blockSize,
            grid.d_current, grid.d_next, LOCAL_WIDTH, LOCAL_HEIGHT, grid.pitch
        );

        // Swap
        std::swap(grid.d_current, grid.d_next);

        // Опционально: синхронизация
        cudaDeviceSynchronize();
    }

    // Копируем результат обратно
    maps::memcpy(h_next_local.data(), grid.d_current,
                 LOCAL_WIDTH * LOCAL_HEIGHT * sizeof(unsigned char), maps::DeviceToHost);

    // Собираем данные с GPU (gather)
    std::vector<unsigned char> h_result_full(GLOBAL_WIDTH * GLOBAL_HEIGHT);
    maps::gather(h_next_local.data(), h_result_full.data(),
                 GLOBAL_WIDTH, GLOBAL_HEIGHT, device_id, num_devices);

    // Регрессионный тест (на GPU 0)
    if (device_id == 0) {
        std::vector<unsigned char> expected(GLOBAL_WIDTH * GLOBAL_HEIGHT, 0);
        // Здесь должен быть эталонный результат (например, из предыдущего запуска)
        // Для примера: просто сравниваем с собой (в реальности — с эталоном)

        if (!compare_grids(h_result_full, expected)) {
            std::string filename = "regression_fail_step_" + std::to_string(TOTAL_ITERATIONS) + "_gpu" + std::to_string(device_id) + ".ppm";
            save_grid_to_ppm(h_result_full, GLOBAL_WIDTH, GLOBAL_HEIGHT, filename);
            std::cerr << "Regression test FAILED. Image saved." << std::endl;
        } else {
            std::cout << "Regression test PASSED." << std::endl;
        }
    }

    // Очистка
    maps::free(grid.d_current);
    maps::free(grid.d_next);
    maps::finalize();

    return 0;
}
```

