void runTest(int width, int height, int iterations) {
    uint8_t* h_input = new uint8_t[width * height];
    uint8_t* h_output = new uint8_t[width * height];
    uint8_t* d_input, *d_output;

    cudaMalloc(&d_input, width * height * sizeof(uint8_t));
    cudaMalloc(&d_output, width * height * sizeof(uint8_t));

    initializeGrid(h_input, width, height);
    cudaMemcpy(d_input, h_input, width * height * sizeof(uint8_t), cudaMemcpyHostToDevice);

    dim3 blockSize(16, 16);
    dim3 gridSize((width + blockSize.x - 1) / blockSize.x, (height + blockSize.y - 1) / blockSize.y);

    for (int i = 0; i < iterations; ++i) {
        golKernel<<<gridSize, blockSize>>>(d_input, d_output, width, height);
        cudaDeviceSynchronize();
        std::swap(d_input, d_output);
    }

    cudaMemcpy(h_output, d_input, width * height * sizeof(uint8_t), cudaMemcpyDeviceToHost);

    // Проверка результата (пример)
