# Реализация симуляции Игры Жизни с использованием MAPS-Multi и CUDA

Для реализации симуляции Игры Жизни (Game of Life, GoL) с поддержкой нескольких GPU, я предоставлю код ядра CUDA и вспомогательных функций с использованием фреймворка MAPS-Multi.

## Основные компоненты реализации

```cpp
#include <cuda_runtime.h>
#include <helper_cuda.h>
#include <maps/multi.cuh>
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <time.h>

// Константы для граничных условий
#define BOUNDARY_WRAP 0
#define BOUNDARY_CLAMP 1

// Структура для хранения параметров симуляции
typedef struct {
    int width;
    int height;
    int iterations;
    int boundary_condition;
    int output_frequency;
} GoLParams;

// Функция ядра CUDA для вычисления следующего состояния
__global__ void gol_kernel(maps::multi::Array<2, unsigned char> input,
                          maps::multi::Array<2, unsigned char> output,
                          int boundary_condition) {
    // Получаем координаты текущей клетки
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= input.size()[0] || y >= input.size()[1]) return;

    int live_neighbors = 0;

    // Проверяем граничные условия и считаем живых соседей
    for (int i = -1; i <= 1; i++) {
        for (int j = -1; j <= 1; j++) {
            if (i == 0 && j == 0) continue; // Пропускаем текущую клетку

            int nx = x + i;
            int ny = y + j;

            // Обработка граничных условий
            if (boundary_condition == BOUNDARY_WRAP) {
                nx = (nx + input.size()[0]) % input.size()[0];
                ny = (ny + input.size()[1]) % input.size()[1];
            } else {
                // BOUNDARY_CLAMP
                if (nx < 0 || nx >= input.size()[0] || ny < 0 || ny >= input.size()[1])
                    continue;
            }

            live_neighbors += input(nx, ny);
        }
    }

    // Применяем правила Игры Жизни
    unsigned char current = input(x, y);
    unsigned char next_state = 0;

    if (current && (live_neighbors == 2 || live_neighbors == 3)) {
        next_state = 1; // Клетка продолжает жить
    } else if (!current && live_neighbors == 3) {
        next_state = 1; // Рождается новая клетка
    }
    // Во всех других случаях клетка умирает или остается мертвой

    output(x, y) = next_state;
}

// Вспомогательная функция для инициализации сетки
void initialize_grid(unsigned char* grid, int width, int height) {
    // Здесь можно реализовать разные начальные конфигурации
    // В данном случае используем случайную заполняемость ~25%
    srand(time(NULL));

    for (int i = 0; i < width * height; i++) {
        grid[i] = (rand() % 100) < 25 ? 1 : 0;
    }
}

// Функция для сохранения состояния в файл (для регрессионных тестов)
void save_state_to_file(const unsigned char* grid, int width, int height, 
                       const char* filename) {
    FILE* fp = fopen(filename, "wb");
    if (fp) {
        fwrite(grid, sizeof(unsigned char), width * height, fp);
        fclose(fp);
    }
}

// Функция для сравнения двух состояний (для регрессионных тестов)
bool compare_states(const unsigned char* state1, const unsigned char* state2, 
                   int width, int height) {
    for (int i = 0; i < width * height; i++) {
        if (state1[i] != state2[i]) {
            printf("Mismatch at position %d\n", i);
            return false;
        }
    }
    return true;
}

// Основная функция симуляции
void run_gol_simulation(GoLParams params) {
    // Выделяем память для хоста
    size_t grid_size = params.width * params.height * sizeof(unsigned char);
    unsigned char* h_grid = (unsigned char*)malloc(grid_size);
    unsigned char* h_grid_out = (unsigned char*)malloc(grid_size);

    // Инициализируем начальное состояние
    initialize_grid(h_grid, params.width, params.height);

    // Создаем массивы MAPS-Multi
    maps::multi::Array<2, unsigned char> d_grid_in(
        {params.width, params.height}, 
        maps::multi::DeviceAllocator(maps::multi::USE_GPU));
    maps::multi::Array<2, unsigned char> d_grid_out(
        {params.width, params.height}, 
        maps::multi::DeviceAllocator(maps::multi::USE_GPU));

    // Копируем начальное состояние на устройство
    d_grid_in.copyFrom(h_grid);

    // Конфигурация запуска ядра
    dim3 block_size(16, 16);
    dim3 grid_size_cuda(
        (params.width + block_size.x - 1) / block_size.x,
        (params.height + block_size.y - 1) / block_size.y);

    // Основной цикл симуляции
    for (int iter = 0; iter < params.iterations; iter++) {
        // Запускаем ядро
        gol_kernel<<<grid_size_cuda, block_size>>>(d_grid_in, d_grid_out, 
                                                 params.boundary_condition);
        checkCudaErrors(cudaDeviceSynchronize());

        // Меняем местами вход и выход для следующей итерации
        std::swap(d_grid_in, d_grid_out);

        // Для проверки результатов периодически сохраняем состояние
        if ((iter + 1) % params.output_frequency == 0) {
            // Копируем результаты на хост
            d_grid_in.copyTo(h_grid_out);

            // Генерируем имя файла
            char filename[256];
            sprintf(filename, "gol_state_%dx%d_iter%d.bin", 
                   params.width, params.height, iter + 1);

            // Сохраняем состояние
            save_state_to_file(h_grid_out, params.width, params.height, filename);
        }
    }

    // Освобождаем ресурсы
    free(h_grid);
    free(h_grid_out);
}

// Функция для регрессионного тестирования
bool regression_test(int width, int height, int iterations, 
                    const char* reference_filename) {
    // Выделяем память
    size_t grid_size = width * height * sizeof(unsigned char);
    unsigned char* h_grid = (unsigned char*)malloc(grid_size);
    unsigned char* h_reference = (unsigned char*)malloc(grid_size);

    // Инициализируем сетку
    initialize_grid(h_grid, width, height);

    // Загружаем эталонное состояние (если есть)
    bool reference_exists = false;
    FILE* fp = fopen(reference_filename, "rb");
    if (fp) {
        size_t read = fread(h_reference, sizeof(unsigned char), width * height, fp);
        fclose(fp);
        reference_exists = (read == width * height);
    }

    // Создаем массивы MAPS-Multi
    maps::multi::Array<2, unsigned char> d_grid_in(
        {width, height}, 
        maps::multi::DeviceAllocator(maps::multi::USE_GPU));
    maps::multi::Array<2, unsigned char> d_grid_out(
        {width, height}, 
        maps::multi::DeviceAllocator(maps::multi::USE_GPU));

    // Копируем начальное состояние на устройство
    d_grid_in.copyFrom(h_grid);

    // Конфигурация запуска ядра
    dim3 block_size(16, 16);
    dim3 grid_size_cuda(
        (width + block_size.x - 1) / block_size.x,
        (height + block_size.y - 1) / block_size.y);

    // Выполняем симуляцию
    for (int iter = 0; iter < iterations; iter++) {
        gol_kernel<<<grid_size_cuda, block_size>>>(d_grid_in, d_grid_out, BOUNDARY_WRAP);
        checkCudaErrors(cudaDeviceSynchronize());
        std::swap(d_grid_in, d_grid_out);
    }

    // Копируем результаты обратно на хост
    d_grid_in.copyTo(h_grid);

    bool test_passed = true;

    if (reference_exists) {
        // Сравниваем с эталоном
        test_passed = compare_states(h_grid, h_reference, width, height);

        if (!test_passed) {
            // Сохраняем изображение различий для анализа
            unsigned char* h_diff = (unsigned char*)malloc(grid_size);
            for (int i = 0; i < width * height; i++) {
                h_diff[i] = (h_grid[i] ^ h_reference[i]) ? 255 : 0;
            }
            save_state_to_file(h_diff, width, height, "gol_test_diff.bin");
            free(h_diff);
        }
    } else {
        // Сохраняем эталонное состояние для будущих тестов
        save_state_to_file(h_grid, width, height, reference_filename);
    }

    // Освобождаем ресурсы
    free(h_grid);
    free(h_reference);

    return test_passed;
}

int main(int argc, char** argv) {
    // Пример параметров симуляции
    GoLParams params = {
        .width = 1024,
        .height = 1024,
        .iterations = 100,
        .boundary_condition = BOUNDARY_WRAP,
        .output_frequency = 10
    };

    // Запускаем симуляцию
    run_gol_simulation(params);

    // Выполняем регрессионный тест
    if (regression_test(256, 256, 50, "gol_reference_256x256_iter50.bin")) {
        printf("Regression test passed successfully!\n");
    } else {
        printf("Regression test failed! Differences saved to gol_test_diff.bin\n");
    }

    return 0;
}
```

## Оптимизации для нескольких GPU

Для эффективного использования нескольких GPU с MAPS-Multi можно модифицировать код следующим образом:

1. **Распределение работы между GPU**:
```cpp
// Разделяем работу по строкам между GPU
int gpu_count;
cudaGetDeviceCount(&gpu_count);

for (int gpu_id = 0; gpu_id < gpu_count; gpu_id++) {
    cudaSetDevice(gpu_id);

    // Вычисляем диапазон строк для текущего GPU
    int rows_per_gpu = (height + gpu_count - 1) / gpu_count;
    int start_row = gpu_id * rows_per_gpu;
    int end_row = min((gpu_id + 1) * rows_per_gpu, height);

    // Создаем подматрицу для текущего GPU
    maps::multi::Array<2, unsigned char> d_subgrid_in = 
        d_grid_in.region({0, start_row}, {width, end_row});
    maps::multi::Array<2, unsigned char> d_subgrid_out = 
        d_grid_out.region({0, start_row}, {width, end_row});

    // Запускаем ядро для подматрицы
    dim3 block_size(16, 16);
    dim3 grid_size_cuda(
        (width + block_size.x - 1) / block_size.x,
        ((end_row - start_row) + block_size.y - 1) / block_size.y);

    gol_kernel<<<grid_size_cuda, block_size>>>(d_subgrid_in, d_subgrid_out, 
                                            params.boundary_condition);
}
```

2. **Обработка границ между GPU**:
```cpp
// После обработки каждого блока строк на GPU, необходимо 
// синхронизировать данные на границах между устройствами
for (int gpu_id = 0; gpu_id < gpu_count; gpu_id++) {
    cudaSetDevice(gpu_id);

    // Копируем граничные строки на соседние GPU
    if (gpu_id > 0) {
        // Копируем верхнюю границу в соседний предыдущий GPU
        auto upper_border = d_grid_in.region({0, 0}, {width, 1});
        auto lower_border_prev = d_grid_in.region({0, -1}, {width, 0});
        lower_border_prev.copyFrom(upper_border);
    }

    if (gpu_id < gpu_count - 1) {
        // Копируем нижнюю границу в соседний следующий GPU
        auto lower_border = d_grid_in.region({0, -1}, {width, 0});
        auto upper_border_next = d_grid_in.region({0, 0}, {width, 1});
        upper_border_next.copyFrom(lower_border);
    }
}
```

