#include <thrust/device_vector.h>
#include <thrust/host_vector.h>
#include <thrust/sort.h>
#include <thrust/sequence.h>
#include <thrust/iterator/transform_iterator.h>
#include <thrust/iterator/counting_iterator.h>
#include <thrust/iterator/permutation_iterator.h>

// Построение графа KNN
void buildKNNGraph(const std::vector<float>& data, int k, thrust::device_vector<int>& indices, thrust::device_vector<float>& distances) {
    // Подсчет количества точек
    int n = data.size();

    // Генерация случайных начальных индексов
    thrust::counting_iterator<int> idx(0);
    thrust::device_vector<int> permutation(n);
    thrust::sequence(idx, idx + n, permutation.begin());

    // Сортируем по расстоянию
    thrust::sort_by_key(thrust::make_permutation_iterator(data.begin(), permutation.begin()),
                        thrust::make_permutation_iterator(data.begin(), permutation.end()),
                        thrust::make_permutation_iterator(indices.begin(), permutation.begin()),
                        thrust::make_permutation_iterator(distances.begin(), permutation.begin()));

    // Отбор k ближайших соседей
    thrust::device_vector<int> selected_indices(k);
    thrust::copy(thrust::make_transform_iterator(permutation.begin(), [&](int i) { return i < k; }), 
                 thrust::make_transform_iterator(permutation.end(), [&](int i) { return i < k; }), 
                 selected_indices.begin());

    // Запись индексов и расстояний
    indices = selected_indices;
    distances = thrust::device_vector<float>(k);
}
```

