### Итоговый код

```c
#include <cstdlib>
#include <cimg.h>
#include <maps/multi/device.h>

__global__ void gameOfLifeKernel(unsigned char *grid, int gridSizeX, int gridSizeY, int neighborhoodSize)
{
    // Определение индекса текущей клетки
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    // Проверка, находится ли клетка в пределах сетки
    if (x < gridSizeX && y < gridSizeY) {
        int neighborCount = 0;

        // Подсчет количества живых соседей
        for (int i = -neighborhoodSize; i <= neighborhoodSize; i++) {
            for (int j = -neighborhoodSize; j <= neighborhoodSize; j++) {
                int neighborX = x + i;
                int neighborY = y + j;

                if (neighborX >= 0 && neighborX < gridSizeX && neighborY >= 0 && neighborY < gridSizeY) {
                    neighborCount += gridneighborX + neighborY * gridSizeX];
                }
            }
        }

        // Применение правил Игры Жизни
        if (grid[x + y * gridSizeX == 1) {
            if (neighborCount < 2 || neighborCount > 3) {
                grid[x + y * gridSizeX = 0;
            }
        } else {
            if (neighborCount == 3) {
                grid[x + y * gridSizeX = 1;
            }
        }
    }
}

void initializeGrid(unsigned char *grid, int gridSizeX, int gridSizeY)
{
    for (int i = 0; i < gridSizeX * gridSizeY; i++) {
        grid[i = (rand() % 2);
    }
}

void updateGrid(unsigned char *grid, int gridSizeX, int gridSizeY, int neighborhoodSize)
{
    dim3 threadsPerBlock(16, 16);
    dim3 blocksPerGrid((gridSizeX + threadsPerBlock.x - 1) / threadsPerBlock.x, (gridSizeY + threadsPerBlock.y - 1) / threadsPerBlock.y);

    gameOfLifeKernel<<<blocksPerGrid, threadsPerBlock>>>(grid, gridSizeX, gridSizeY, neighborhoodSize);
}

void saveImage(const char *filename, unsigned char *grid, int gridSizeX, int gridSizeY)
{
    cimg_library::CImg<unsigned char> img(gridSizeX, gridSizeY, 1, 1, grid);
    img.save(filename);
}

void runOnMultipleGPUs() {
    int gridSizeX = 100;
    int gridSizeY = 100;
    int neighborhoodSize = 1;

    unsigned char *grid = (unsigned char *)malloc(gridSizeX * gridSizeY);
    initializeGrid(grid, gridSizeX, gridSizeY);

    maps::multi::Device device;
    maps::multi::Context context;

    // Создание нескольких потоков
    int numGPUs = device.getNumDevices();
    for (int i = 0; i < numGPUs; i++) {
        context = device.createContext(i);
        updateGrid(grid, gridSizeX, gridSizeY, neighborhoodSize);
        context.synchronize();
    }

    saveImage("output.png", grid, gridSizeX, gridSizeY);
}

int main() {
    runOnMultipleGPUs();
    return 0;
}
```

Этот код реализует симуляцию Игры Жизни с использованием фреймворка MAPS-Multi и CUDA. Он позволяет выполнять параллельные вычисления на нескольких GPU и сохранять результаты в виде изображений.

