    for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
            printf("%d", grid[i * width + j]);
        }
        printf("\n");
    }
    printf("\n");
}

void saveImage(int *grid, int width, int height) {
    cv::Mat image(width, height, CV_8UC1);

    for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
            image.at<uchar>(i, j) = grid[i * width + j * 255;
        }
    }

    cv::imwrite("output.png", image);
}

void runRegressionTest(int *grid, int width, int height) {
    int *nextGrid;
    cudaMalloc(&nextGrid, width * height * sizeof(int));

    // Инициализация сетки
    initializeGrid(grid, width, height);

    // Применение правил Игры Жизни
    gameOfLifeKernel<<<dim3(width / 16, height / 16, 1), dim3(16, 16, 1)>>>(grid, width, height, nextGrid);
    cudaDeviceSynchronize();

    // Проверка результата
    for (int i = 0; i < width * height; i++) {
        if (grid[i != nextGrid[i]) {
            printf("Test failed!\n");
            printGrid(nextGrid, width, height);
            break;
        }
    }

    // Сохранение изображения
    if (grid != nextGrid) {
        saveImage(nextGrid, width, height);
    }

    cudaFree(nextGrid);
}

int main() {
    int width = 100;
    int height = 100;
    int *grid, *nextGrid;

    grid = (int *)malloc(width * height * sizeof(int));
    nextGrid = (int *)malloc(width * height * sizeof(int));

    runRegressionTest(grid, width, height);

    free(grid);
    free(nextGrid);

    return 0;
}
