    maps::multi::device::Grid<int> *deviceGrid = new maps::multi::device::Grid<int>(width, height, gridSize);

    // Запуск ядра на всех GPU
    for (int i = 0; i < numGpus; i++) {
        int blockSizeX = 32;
        int blockSizeY = 32;
        int gridSizeX = (width + blockSizeX - 1) / blockSizeX;
        int gridSizeY = (height + blockSizeY - 1) / blockSizeY;

        dim3 block(blockSizeX, blockSizeY);
        dim3 grid(gridSizeX, gridSizeY);

        gameOfLifeKernel<<<grid, block>>>(deviceGrid, gridSize);
    }

    // Копирование обновленного состояния обратно на хост
    deviceGrid->copyToHost();
}
```

