package main

import (
	"crypto/tls"
	"net/http"
	"net/url"
	"time"

	"github.com/elastic/go-elasticsearch/v8"
)

// TransportBuilder - структура для настройки HTTP-транспорта
type TransportBuilder struct {
	proxyURL          *url.URL
	proxyUsername     string
	proxyPassword     string
	useSystemProxy    bool
	authUsername      string
	authPassword      string
	tlsConfig         *tls.Config
	headers           http.Header
	clientTimeout     time.Duration
}

// Build создает HTTP-транспорт для Elasticsearch
func (b *TransportBuilder) Build() (*elasticsearch.Transport, error) {
	// Создаем базовый HTTP клиент
	client := &http.Client{
		Timeout: b.clientTimeout,
	}

	// Настраиваем прокси
	if b.proxyURL != nil {
		proxyTransport := &http.Transport{
			Proxy: http.ProxyURL(b.proxyURL),
		}

		// Добавляем базовую аутентификацию для прокси, если указаны логин и пароль
		if b.proxyUsername != "" && b.proxyPassword != "" {
			proxyTransport.Proxy = func(req *http.Request) (*url.URL, error) {
				req.SetBasicAuth(b.proxyUsername, b.proxyPassword)
				return b.proxyURL, nil
			}
		}

		client.Transport = proxyTransport
	} else if !b.useSystemProxy {
		// Отключаем прокси, если явно указано
		client.Transport = &http.Transport{
			Proxy: func(req *http.Request) (*url.URL, error) {
				return nil, nil
			},
		}
	}

	// Настраиваем TLS
	if b.tlsConfig != nil {
		client.Transport = &http.Transport{
			TLSClientConfig: b.tlsConfig,
		}
	}

	// Создаем транспорт для Elasticsearch
	transport, err := elasticsearch.NewTransport(
		client,
		elasticsearch.SetURL("http://localhost:9200"), // URL можно передавать отдельно или через builder
		elasticsearch.SetBasicAuth(b.authUsername, b.authPassword),
		elasticsearch.SetHeaders(b.headers),
	)
	if err != nil {
		return nil, err
	}

	return transport, nil
}
```

