# Реализация Игры Жизни (Game of Life) с использованием CUDA и MAPS-Multi

Вот реализация симуляции Игры Жизни с использованием CUDA, которая может быть интегрирована в фреймворк MAPS-Multi для тестирования производительности на нескольких GPU.

## Основное CUDA ядро

```cpp
#include <cuda_runtime.h>
#include <iostream>
#include <fstream>
#include <vector>
#include <cassert>

// Функция ядра для вычисления следующего состояния клетки
__global__ void gameOfLifeKernel(unsigned char* current, unsigned char* next,
                                int width, int height, int pitch) {
    // Вычисляем индекс текущей клетки
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    // Проверяем, что координаты в пределах сетки
    if (x >= width || y >= height) return;

    // Вычисляем количество живых соседей
    int count = 0;
    for (int dy = -1; dy <= 1; dy++) {
        for (int dx = -1; dx <= 1; dx++) {
            if (dx == 0 && dy == 0) continue; // Пропускаем саму клетку

            int nx = x + dx;
            int ny = y + dy;

            // Проверяем границы сетки
            if (nx >= 0 && nx < width && ny >= 0 && ny < height) {
                // Используем pitch для правильного доступа к памяти
                count += current[ny * pitch / sizeof(unsigned char) + nx];
            }
        }
    }

    // Применяем правила Игры Жизни
    unsigned char currentState = current[y * pitch / sizeof(unsigned char) + x];
    if (currentState) {
        // Живая клетка умирает, если у нее меньше 2 или больше 3 соседей
        next[y * pitch / sizeof(unsigned char) + x] = (count == 2 || count == 3) ? 1 : 0;
    } else {
        // Мертвая клетка оживает, если у нее ровно 3 соседа
        next[y * pitch / sizeof(unsigned char) + x] = (count == 3) ? 1 : 0;
    }
}

// Функция для выполнения одного шага симуляции
void gameOfLifeStep(unsigned char* d_current, unsigned char* d_next,
                    int width, int height, int pitch) {
    // Определяем размер блока и сетки
    dim3 blockSize(16, 16);
    dim3 gridSize((width + blockSize.x - 1) / blockSize.x,
                  (height + blockSize.y - 1) / blockSize.y);

    // Запускаем ядро
    gameOfLifeKernel<<<gridSize, blockSize>>>(d_current, d_next, width, height, pitch);

    // Проверяем на ошибки
    cudaError_t err = cudaGetLastError();
    if (err != cudaSuccess) {
        std::cerr << "CUDA error: " << cudaGetErrorString(err) << std::endl;
    }
}

// Функция для инициализации сетки с заданным паттерном
void initializeGrid(unsigned char* grid, int width, int height, int pitch,
                   const std::vector<std::pair<int, int>>& liveCells) {
    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            grid[y * pitch / sizeof(unsigned char) + x] = 0;
        }
    }

    for (const auto& cell : liveCells) {
        int x = cell.first;
        int y = cell.second;
        if (x >= 0 && x < width && y >= 0 && y < height) {
            grid[y * pitch / sizeof(unsigned char) + x] = 1;
        }
    }
}

// Функция для сохранения состояния сетки в файл
void saveGridToFile(const unsigned char* grid, int width, int height, int pitch,
                   const std::string& filename) {
    std::ofstream outFile(filename, std::ios::binary);
    if (!outFile) {
        std::cerr << "Failed to open file: " << filename << std::endl;
        return;
    }

    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            outFile << grid[y * pitch / sizeof(unsigned char) + x];
        }
    }
    outFile.close();
}

// Функция для сравнения двух состояний сетки
bool compareGrids(const unsigned char* grid1, const unsigned char* grid2,
                 int width, int height, int pitch) {
    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            if (grid1[y * pitch / sizeof(unsigned char) + x] !=
                grid2[y * pitch / sizeof(unsigned char) + x]) {
                return false;
            }
        }
    }
    return true;
}

// Функция для выполнения регрессионного теста
bool runRegressionTest(int width, int height, int steps,
                      const std::vector<std::pair<int, int>>& initialPattern,
                      const std::vector<std::pair<int, int>>& expectedPattern) {
    // Выделяем память на GPU
    unsigned char* d_current;
    unsigned char* d_next;
    unsigned char* d_expected;

    size_t pitch;
    cudaMallocPitch(&d_current, &pitch, width * sizeof(unsigned char), height);
    cudaMallocPitch(&d_next, &pitch, width * sizeof(unsigned char), height);
    cudaMallocPitch(&d_expected, &pitch, width * sizeof(unsigned char), height);

    // Выделяем память на CPU для тестового паттерна
    std::vector<unsigned char> h_expected(width * height, 0);
    for (const auto& cell : expectedPattern) {
        int x = cell.first;
        int y = cell.second;
        if (x >= 0 && x < width && y >= 0 && y < height) {
            h_expected[y * width + x] = 1;
        }
    }

    // Копируем ожидаемый паттерн на GPU
    cudaMemcpy2D(d_expected, pitch, h_expected.data(), width * sizeof(unsigned char),
                 width * sizeof(unsigned char), height, cudaMemcpyHostToDevice);

    // Инициализируем текущую сетку
    std::vector<unsigned char> h_current(width * height, 0);
    for (const auto& cell : initialPattern) {
        int x = cell.first;
        int y = cell.second;
        if (x >= 0 && x < width && y >= 0 && y < height) {
            h_current[y * width + x] = 1;
        }
    }

    // Копируем текущую сетку на GPU
    cudaMemcpy2D(d_current, pitch, h_current.data(), width * sizeof(unsigned char),
                 width * sizeof(unsigned char), height, cudaMemcpyHostToDevice);

    // Выполняем шаги симуляции
    for (int step = 0; step < steps; step++) {
        gameOfLifeStep(d_current, d_next, width, height, pitch);

        // Меняем местами текущую и следующую сетки
        unsigned char* temp = d_current;
        d_current = d_next;
        d_next = temp;
    }

    // Копируем результат обратно на CPU
    std::vector<unsigned char> h_result(width * height);
    cudaMemcpy2D(h_result.data(), width * sizeof(unsigned char), d_current, pitch,
                 width * sizeof(unsigned char), height, cudaMemcpyDeviceToHost);

    // Сравниваем с ожидаемым результатом
    bool passed = compareGrids(h_result.data(), h_expected.data(), width, height, pitch);

    // Освобождаем память
    cudaFree(d_current);
    cudaFree(d_next);
    cudaFree(d_expected);

    return passed;
}

