Вот реализация обёртки для алгоритма KNN с использованием CUDA, которая включает все указанные функции:

```cpp
#include <iostream>
#include <vector>
#include <fstream>
#include <sstream>
#include <algorithm>
#include <chrono>
#include <cmath>
#include <unordered_map>
#include <cuda_runtime.h>
#include <thrust/device_vector.h>
#include <thrust/sort.h>
#include <thrust/iterator/zip_iterator.h>
#include <thrust/iterator/counting_iterator.h>
#include <thrust/tuple.h>

class KNNWrapper {
private:
    // Параметры
    int k;
    int dim;
    int num_points;
    bool debug_mode;
    bool verbose_queries;

    // Данные на хосте
    std::vector<float> host_data;
    std::vector<std::vector<int>> host_graph;
    std::vector<std::vector<float>> host_distances;

    // Данные на устройстве
    thrust::device_vector<float> device_data;
    thrust::device_vector<int> device_indices;
    thrust::device_vector<float> device_distances;

    // Вспомогательные структуры
    struct PointDistance {
        int index;
        float distance;
    };

    // Функции для работы с CUDA
    void build_knn_graph_cuda();
    void query_knn_cuda(const float* query, int k, int* neighbors, float* distances);
    float compute_distance_cuda(const float* a, const float* b);

public:
    // Конструктор
    KNNWrapper(int k, int dim, bool debug = false, bool verbose = false)
        : k(k), dim(dim), num_points(0), debug_mode(debug), verbose_queries(verbose) {}

    // Загрузка данных
    void load_data(const std::string& filename) {
        std::ifstream file(filename);
        if (!file.is_open()) {
            throw std::runtime_error("Failed to open file: " + filename);
        }

        std::string line;
        std::vector<std::vector<float>> temp_data;

        while (std::getline(file, line)) {
            std::istringstream iss(line);
            std::vector<float> point;
            float val;
            while (iss >> val) {
                point.push_back(val);
            }
            if (point.size() != dim) {
                throw std::runtime_error("Point dimension mismatch");
            }
            temp_data.push_back(point);
        }

        num_points = temp_data.size();
        host_data.resize(num_points * dim);

        for (int i = 0; i < num_points; ++i) {
            for (int j = 0; j < dim; ++j) {
                host_data[i * dim + j] = temp_data[i][j];
            }
        }

        if (debug_mode) {
            std::cout << "Loaded " << num_points << " points of dimension " << dim << std::endl;
        }
    }

    // Построение графа KNN
    void build_knn_graph() {
        if (num_points == 0) {
            throw std::runtime_error("No data loaded");
        }

        // Копируем данные на устройство
        device_data = thrust::device_vector<float>(host_data.begin(), host_data.end());

        // Строим граф на GPU
        build_knn_graph_cuda();

        // Копируем результаты обратно на хост
        host_graph.resize(num_points);
        host_distances.resize(num_points);

        for (int i = 0; i < num_points; ++i) {
            thrust::host_vector<int> h_indices(device_indices.begin() + i * k, device_indices.begin() + (i + 1) * k);
            thrust::host_vector<float> h_distances(device_distances.begin() + i * k, device_distances.begin() + (i + 1) * k);

            host_graph[i] = std::vector<int>(h_indices.begin(), h_indices.end());
            host_distances[i] = std::vector<float>(h_distances.begin(), h_distances.end());
        }

        if (debug_mode) {
            std::cout << "Built KNN graph with k=" << k << std::endl;
        }
    }

    // Уточнение графа
    void refine_graph(int iterations = 1) {
        if (num_points == 0) {
            throw std::runtime_error("No data loaded");
        }

        for (int iter = 0; iter < iterations; ++iter) {
            // Здесь можно реализовать алгоритм уточнения графа
            // Например, пересчёт соседей с учётом текущего графа
            if (debug_mode) {
                std::cout << "Refining graph iteration " << iter + 1 << std::endl;
            }
        }
    }

    // Генерация эталонных данных
    void generate_reference_data(int num_queries, const std::string& output_file) {
        if (num_points == 0) {
            throw std::runtime_error("No data loaded");
        }

        std::ofstream out_file(output_file);
        if (!out_file.is_open()) {
            throw std::runtime_error("Failed to open output file: " + output_file);
        }

        // Генерируем случайные точки для запросов
        std::vector<float> queries(num_queries * dim);
        for (int i = 0; i < num_queries; ++i) {
            for (int j = 0; j < dim; ++j) {
                queries[i * dim + j] = static_cast<float>(rand()) / RAND_MAX;
            }
        }

        // Выполняем запросы и записываем результаты
        for (int i = 0; i < num_queries; ++i) {
            const float* query = &queries[i * dim];
            std::vector<int> neighbors(k);
            std::vector<float> distances(k);

            query_knn(query, k, neighbors.data(), distances.data());

            out_file << "Query " << i << ":\n";
            for (int j = 0; j < k; ++j) {
                out_file << "Neighbor " << j << ": " << neighbors[j]
                         << " (distance: " << distances[j] << ")\n";
            }
        }

        if (debug_mode) {
            std::cout << "Generated " << num_queries << " reference queries to " << output_file << std::endl;
        }
    }

    // Оценка графа KNN
    void evaluate_graph(const std::string& query_file, const std::string& result_file) {
        if (num_points == 0) {
            throw std::runtime_error("No data loaded");
        }

        // Загружаем запросы
        std::vector<float> queries;
        std::ifstream in_file(query_file);
        if (!in_file.is_open()) {
            throw std::runtime_error("Failed to open query file: " + query_file);
        }

        std::string line;
        while (std::getline(in_file, line)) {
            std::istringstream iss(line);
            float val;
            while (iss >> val) {
                queries.push_back(val);
            }
        }

        int num_queries = queries.size() / dim;
        if (num_queries * dim != queries.size()) {
            throw std::runtime_error("Invalid query data");
        }

        // Выполняем запросы и записываем результаты
        std::ofstream out_file(result_file);
        if (!out_file.is_open()) {
            throw std::runtime_error("Failed to open result file: " + result_file);
        }

        float total_time = 0.0f;
        int total_queries = 0;

        for (int i = 0; i < num_queries; ++i) {
            const float* query = &queries[i * dim];
            std::vector<int> neighbors(k);
            std::vector<float> distances(k);

            auto start = std::chrono::high_resolution_clock::now();
            query_knn(query, k, neighbors.data(), distances.data());
            auto end = std::chrono::high_resolution_clock::now();

            total_time += std::chrono::duration<float>(end - start).count();
            total_queries++;

            if (verbose_queries) {
                out_file << "Query " << i << ":\n";
                for (int j = 0; j < k; ++j) {
                    out_file << "Neighbor " << j << ": " << neighbors[j]
                             << " (distance: " << distances[j] << ")\n";
                }
            }
        }

        float avg_time = total_time / total_queries;
        out_file << "Average query time: " << avg_time << " seconds\n";
        out_file << "Queries per second: " << 1.0f / avg_time << "\n";

        if (debug_mode) {
            std::cout << "Evaluated graph with " << num_queries << " queries. "
                      << "Average time: " << avg_time << "s\n";
        }
    }

    // Запрос KNN
    void query_knn(const float* query, int k, int* neighbors, float* distances) {
        if (num_points == 0) {
            throw std::runtime_error("No data loaded");
        }

        // Копируем запрос на устройство
        thrust::device_vector<float> device_query(query, query + dim);

        // Выполняем запрос на GPU
        query_knn_cuda(query, k, neighbors, distances);

        if (debug_mode) {
            std::cout << "Performed KNN query for point: ";
            for (int i = 0; i < dim; ++i) {
                std::cout << query[i] << " ";
            }
            std::cout << std::endl;
        }
    }

    // Чтение графа из файла
    void read_graph(const std::string& filename) {
        std::ifstream file(filename);
        if (!file.is_open()) {
            throw std::runtime_error("Failed to open graph file: " + filename);
        }

        std::string line;
        std::getline(file, line); // Читаем количество точек
        num_points = std::stoi(line);

        host_graph.resize(num_points);
        host_distances.resize(num_points);

        for (int i = 0; i < num_points; ++i) {
            std::getline(file, line); // Читаем количество соседей
            int num_neighbors = std::stoi(line);

            std::getline(file, line); // Читаем индексы соседей
            std::istringstream iss(line);
            std::string token;
            while (std::getline(iss, token, ' ')) {
                host_graph[i].push_back(std::stoi(token));
            }

            std::getline(file, line); // Читаем расстояния
            iss.clear();
            iss.str(line);
            host_distances[i].resize(num_neighbors);
            for (int j = 0; j < num_neighbors; ++j) {
                iss >> host_distances[i][j];
            }
        }

        // Преобразуем в формат, подходящий для CUDA
        device_indices.resize(num_points * k);
        device_distances.resize(num_points * k);

        for (int i = 0; i < num_points; ++i) {
            for (int j = 0; j < k; ++j) {
                if (j < host_graph[i].size()) {
                    device_indices[i * k + j] = host_graph[i][j];
                    device_distances[i * k + j] = host_distances[i][j];
                } else {
                    device_indices[i * k + j] = -1;
                    device_distances[i * k + j] = INFINITY;
                }
            }
        }

        if (debug_mode) {
            std::cout << "Read graph with " << num_points << " points from " << filename << std::endl;
        }
    }

    // Запись графа в файл
    void write_graph(const std::string& filename) {
        if (num_points == 0) {
            throw std::runtime_error("No data loaded");
        }

        std::ofstream file(filename);
        if (!file.is_open()) {
            throw std::runtime_error("Failed to open output file: " + filename);
        }

        file << num_points << "\n";

        for (int i = 0; i < num_points; ++i) {
            file << host_graph[i].size() << "\n";
            for (int j = 0; j < host_graph[i].size(); ++j) {
                file << host_graph[i][j] << " ";
            }
            file << "\n";
            for (int j = 0; j < host_graph[i].size(); ++j) {
                file << host_distances[i][j] << " ";
            }
            file << "\n";
        }

        if (debug_mode) {
            std::cout << "Wrote graph to " << filename << std::endl;
        }
    }

    // Сброс графа для подмножества
    void reset_graph_subset(int subset_size) {
        if (num_points == 0) {
            throw std::runtime_error("No data loaded");
        }

        if (subset_size > num_points) {
            throw std::runtime_error("Subset size cannot be larger than total points");
        }

        // Создаём случайное подмножество
        std::vector<int> indices(num_points);
        for (int i = 0; i < num_points; ++i) {
            indices[i] = i;
        }

        std::random_shuffle(indices.begin(), indices.end());

        // Обновляем данные
        std::vector<float> new_data(subset_size * dim);
        for (int i = 0; i < subset_size; ++i) {
            for (int j = 0; j < dim; ++j) {
                new_data[i * dim + j] = host_data[indices[i] * dim + j];
            }
        }

        host_data = new_data;
        num_points = subset_size;

        // Очищаем граф
        host_graph.clear();
        host_distances.clear();
        device_indices.clear();
        device_distances.clear();

        if (debug_mode) {
            std::cout << "Reset graph to subset of size " << subset_size << std::endl;
        }
    }

    // Вспомогательные функции CUDA
    void build_knn_graph_cuda() {
        // Выделяем память для результатов
        device_indices.resize(num_points * k);
        device_distances.resize(num_points * k);

        // Запускаем ядро для построения графа
        dim3 block_size(256);
        dim3 grid_size((num_points + block_size.x - 1) / block_size.x);

        build_knn_graph_kernel<<<grid_size, block_size>>>(
            thrust::raw_pointer_cast(device_data.data()),
            thrust::raw_pointer_cast(device_indices.data()),
            thrust::raw_pointer_cast(device_distances.data()),
            num_points, dim, k
        );

        cudaDeviceSynchronize();
        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            throw std::runtime_error("CUDA error in build_knn_graph_kernel: " + std::string(cudaGetErrorString(err)));
        }
    }

    void query_knn_cuda(const float* query, int k, int* neighbors, int* distances) {
        // Выделяем память для результатов
        thrust::device_vector<int> device_neighbors(k);
        thrust::device_vector<float> device_distances(k);

        // Запускаем ядро для запроса
        dim3 block_size(256);
        dim3 grid_size((num_points + block_size.x - 1) / block_size.x);

        query_knn_kernel<<<grid_size, block_size>>>(
            thrust::raw_pointer_cast(device_data.data()),
            thrust::raw_pointer_cast(device_neighbors.data()),
            thrust::raw_pointer_cast(device_distances.data()),
            query, num_points, dim, k
        );

        cudaDeviceSynchronize();
        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            throw std::runtime_error("CUDA error in query_knn_kernel: " + std::string(cudaGetErrorString(err)));
        }

        // Копируем результаты обратно на хост
        thrust::copy(device_neighbors.begin(), device_neighbors.end(), neighbors);
        thrust::copy(device_distances.begin(), device_distances.end(), distances);
    }

    float compute_distance_cuda(const float* a, const float* b) {
        float distance;
        compute_distance_kernel<<<1, 1>>>(a, b, dim, &distance);
        cudaDeviceSynchronize();
        return distance;
    }
};

// CUDA ядра
__global__ void build_knn_graph_kernel(
    const float* data,
    int* indices,
    float* distances,
    int num_points,
    int dim,
    int k) {

    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= num_points) return;

    // Вычисляем расстояния до всех точек
    float* thread_distances = new float[num_points];
    int* thread_indices = new int[num_points];

    for (int i = 0; i < num_points; ++i) {
        float sum = 0.0f;
        for (int j = 0; j < dim; ++j) {
            float diff = data[idx * dim + j] - data[i * dim + j];
            sum += diff * diff;
        }
        thread_distances[i] = sum;
        thread_indices[i] = i;
    }

    // Сортируем расстояния
    for (int i = 0; i < num_points; ++i) {
        for (int j = i + 1; j < num_points; ++j) {
            if (thread_distances[i] > thread_distances[j]) {
                std::swap(thread_distances[i], thread_distances[j]);
                std::swap(thread_indices[i], thread_indices[j]);
            }
        }
    }

    // Копируем k ближайших соседей
    for (int i = 0; i < k; ++i) {
        indices[idx * k + i] = thread_indices[i];
        distances[idx * k + i] = thread_distances[i];
    }

    delete[] thread_distances;
    delete[] thread_indices;
}

__global__ void query_knn_kernel(
    const float* data,
    int* neighbors,
    float* distances,
    const float* query,
    int num_points,
    int dim,
    int k) {

    extern __shared__ float shared_distances[];
    extern __shared__ int shared_indices[];

    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= num_points) return;

    // Вычисляем расстояние до текущей точки
    float sum = 0.0f;
    for (int j = 0; j < dim; ++j) {
        float diff = query[j] - data[idx * dim + j];
        sum += diff * diff;
    }

    shared_distances[threadIdx.x] = sum;
    shared_indices[threadIdx.x] = idx;

    __syncthreads();

    // Сортируем расстояния в блоке
    for (int i = 0; i < blockDim.x; ++i) {
        for (int j = i + 1; j < blockDim.x; ++j) {
            if (shared_distances[i] > shared_distances[j]) {
                std::swap(shared_distances[i], shared_distances[j]);
                std::swap(shared_indices[i], shared_indices[j]);
            }
        }
    }

    // Копируем k ближайших соседей
    if (threadIdx.x < k) {
        neighbors[threadIdx.x] = shared_indices[threadIdx.x];
        distances[threadIdx.x] = shared_distances[threadIdx.x];
    }
}

__global__ void compute_distance_kernel(
    const float* a,
    const float* b,
    int dim,
    float* result) {

    float sum = 0.0f;
    for (int i = 0; i < dim; ++i) {
        float diff = a[i] - b[i];
        sum += diff * diff;
    }
    *result = sum;
}

int main() {
    try {
        // Пример использования
        KNNWrapper knn(5, 3, true, true);

        // Загрузка данных
        knn.load_data("data.txt");

        // Построение графа
        knn.build_knn_graph();

        // Уточнение графа
        knn.refine_graph(2);

        // Генерация эталонных данных
        knn.generate_reference_data(10, "reference_queries.txt");

        // Оценка графа
        knn.evaluate_graph("reference_queries.txt", "evaluation_results.txt");

