        float query[3] = {0.1f, 0.2f, 0.3f};
        std::vector<int> neighbors(5);
        std::vector<float> distances(5);
        knn.query_knn(query, 5, neighbors.data(), distances.data());

        // Чтение и запись графа
        knn.write_graph("knn_graph.txt");
        knn.read_graph("knn_graph.txt");

