# Обёртка для алгоритма KNN с использованием CUDA

Вот реализация обёртки для алгоритма KNN с использованием CUDA, которая включает все указанные функции:

```cpp
#include <iostream>
#include <vector>
#include <fstream>
#include <sstream>
#include <algorithm>
#include <chrono>
#include <cmath>
#include <unordered_map>
#include <cuda_runtime.h>
#include <thrust/device_vector.h>
#include <thrust/sort.h>
#include <thrust/iterator/zip_iterator.h>
#include <thrust/iterator/counting_iterator.h>
#include <thrust/tuple.h>

class KNNWrapper {
private:
    // Параметры графа
    int k; // количество ближайших соседей
    int dim; // размерность точек
    int num_points; // количество точек в графе

    // Данные на хосте
    std::vector<float> host_data;
    std::vector<std::vector<int>> host_graph;

    // Данные на устройстве
    thrust::device_vector<float> device_data;
    thrust::device_vector<int> device_graph;

    // Флаги отладки
    bool debug_mode = false;
    bool verbose_queries = false;

    // Вспомогательные функции
    void printDebugInfo(const std::string& message) {
        if (debug_mode) {
            std::cout << "[DEBUG] " << message << std::endl;
        }
    }

    void printVerboseQueryInfo(const std::string& message) {
        if (verbose_queries) {
            std::cout << "[QUERY] " << message << std::endl;
        }
    }

    // Вычисление расстояния между двумя точками
    __device__ float calculateDistance(const float* a, const float* b, int dim) {
        float sum = 0.0f;
        for (int i = 0; i < dim; ++i) {
            float diff = a[i] - b[i];
            sum += diff * diff;
        }
        return sqrtf(sum);
    }

    // Ядро для построения графа KNN
    __global__ void buildKNNGraphKernel(const float* data, int* graph, int num_points, int dim, int k) {
        int idx = blockIdx.x * blockDim.x + threadIdx.x;
        if (idx >= num_points) return;

        // Временный буфер для хранения расстояний и индексов
        struct {
            float distance;
            int index;
        } *temp = new struct {
            float distance;
            int index;
        }[num_points - 1];

        // Вычисляем расстояния до всех других точек
        for (int i = 0; i < num_points; ++i) {
            if (i == idx) continue;

            float dist = calculateDistance(&data[idx * dim], &data[i * dim], dim);
            temp[i > idx ? i - 1 : i] = {dist, i};
        }

        // Сортируем по расстоянию
        thrust::sort(
            thrust::device,
            temp,
            temp + (num_points - 1),
            thrust::less<float>()
        );

        // Записываем k ближайших соседей
        for (int i = 0; i < k; ++i) {
            graph[idx * k + i] = temp[i].index;
        }

        delete[] temp;
    }

    // Ядро для запроса KNN
    __global__ void queryKNNKernel(const float* data, const int* graph, const float* query,
                                  int* results, float* distances, int num_points, int dim, int k) {
        int idx = blockIdx.x * blockDim.x + threadIdx.x;
        if (idx >= num_points) return;

        // Вычисляем расстояния до всех соседей текущей точки
        for (int i = 0; i < k; ++i) {
            int neighbor_idx = graph[idx * k + i];
            float dist = calculateDistance(query, &data[neighbor_idx * dim], dim);
            results[idx * k + i] = neighbor_idx;
            distances[idx * k + i] = dist;
        }
    }

public:
    // Конструктор
    KNNWrapper(int k, int dim, bool debug = false, bool verbose = false)
        : k(k), dim(dim), num_points(0), debug_mode(debug), verbose_queries(verbose) {
        printDebugInfo("Created KNNWrapper with k=" + std::to_string(k) + ", dim=" + std::to_string(dim));
    }

    // Загрузка данных из файла
    bool loadDataFromFile(const std::string& filename) {
        printDebugInfo("Loading data from file: " + filename);

        std::ifstream file(filename);
        if (!file.is_open()) {
            std::cerr << "Error: Could not open file " << filename << std::endl;
            return false;
        }

        host_data.clear();
        std::string line;
        while (std::getline(file, line)) {
            std::istringstream iss(line);
            float value;
            while (iss >> value) {
                host_data.push_back(value);
            }
        }

        num_points = host_data.size() / dim;
        printDebugInfo("Loaded " + std::to_string(num_points) + " points with dimension " + std::to_string(dim));

        return true;
    }

    // Построение графа KNN
    bool buildGraph() {
        if (host_data.empty()) {
            std::cerr << "Error: No data loaded" << std::endl;
            return false;
        }

        printDebugInfo("Building KNN graph with k=" + std::to_string(k));

        // Копируем данные на устройство
        device_data = thrust::device_vector<float>(host_data.begin(), host_data.end());

        // Выделяем память для графа на устройстве
        device_graph.resize(num_points * k);

        // Запускаем ядро для построения графа
        int block_size = 256;
        int grid_size = (num_points + block_size - 1) / block_size;

        buildKNNGraphKernel<<<grid_size, block_size>>>(thrust::raw_pointer_cast(device_data.data()),
                                                      thrust::raw_pointer_cast(device_graph.data()),
                                                      num_points, dim, k);

        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            std::cerr << "CUDA error during graph building: " << cudaGetErrorString(err) << std::endl;
            return false;
        }

        // Копируем граф обратно на хост
        host_graph.resize(num_points);
        for (int i = 0; i < num_points; ++i) {
            host_graph[i].resize(k);
            for (int j = 0; j < k; ++j) {
                host_graph[i][j] = device_graph[i * k + j];
            }
        }

        printDebugInfo("Graph built successfully");
        return true;
    }

    // Уточнение графа (например, после добавления новых точек)
    bool refineGraph() {
        printDebugInfo("Refining KNN graph");
        // В реальной реализации здесь должна быть логика уточнения графа
        // Например, пересчёт соседей для новых точек
        return true;
    }

    // Генерация эталонных данных для тестирования
    void generateReferenceData(int num_points, int dim) {
        printDebugInfo("Generating reference data with " + std::to_string(num_points) +
                      " points and dimension " + std::to_string(dim));

        this->dim = dim;
        this->num_points = num_points;
        host_data.resize(num_points * dim);

        for (int i = 0; i < num_points; ++i) {
            for (int j = 0; j < dim; ++j) {
                host_data[i * dim + j] = static_cast<float>(rand()) / RAND_MAX;
            }
        }
    }

    // Оценка качества графа KNN
    float evaluateGraph() {
        printDebugInfo("Evaluating graph quality");

        // В реальной реализации здесь должна быть логика оценки графа
        // Например, проверка, что соседи действительно ближайшие
        return 0.0f; // Возвращаем фиктивное значение
    }

    // Запрос KNN для заданной точки
    std::vector<std::pair<int, float>> queryKNN(const std::vector<float>& query_point) {
        if (query_point.size() != dim) {
            std::cerr << "Error: Query point dimension mismatch" << std::endl;
            return {};
        }

        printVerboseQueryInfo("Processing query for point: ");
        for (float val : query_point) {
            printVerboseQueryInfo(std::to_string(val) + " ");
        }
        printVerboseQueryInfo("");

        // Копируем запрос на устройство
        thrust::device_vector<float> device_query(query_point.begin(), query_point.end());

        // Выделяем память для результатов
        thrust::device_vector<int> device_results(num_points * k);
        thrust::device_vector<float> device_distances(num_points * k);

        // Запускаем ядро для запроса
        int block_size = 256;
        int grid_size = (num_points + block_size - 1) / block_size;

        queryKNNKernel<<<grid_size, block_size>>>(thrust::raw_pointer_cast(device_data.data()),
                                                 thrust::raw_pointer_cast(device_graph.data()),
                                                 thrust::raw_pointer_cast(device_query.data()),
                                                 thrust::raw_pointer_cast(device_results.data()),
                                                 thrust::raw_pointer_cast(device_distances.data()),
                                                 num_points, dim, k);

        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            std::cerr << "CUDA error during query: " << cudaGetErrorString(err) << std::endl;
            return {};
        }

        // Копируем результаты обратно на хост
        std::vector<std::pair<int, float>> results;
        for (int i = 0; i < k; ++i) {
            results.emplace_back(device_results[i], device_distances[i]);
        }

        // Сортируем результаты по расстоянию
        std::sort(results.begin(), results.end(),
            [](const auto& a, const auto& b) { return a.second < b.second; });

        printVerboseQueryInfo("Query results:");
        for (const auto& res : results) {
            printVerboseQueryInfo("Neighbor " + std::to_string(res.first) +
                                 " at distance " + std::to_string(res.second));
        }

        return results;
    }

    // Сохранение графа в файл
    bool saveGraphToFile(const std::string& filename) {
        printDebugInfo("Saving graph to file: " + filename);

        std::ofstream file(filename);
        if (!file.is_open()) {
            std::cerr << "Error: Could not open file " << filename << std::endl;
            return false;
        }

        for (const auto& neighbors : host_graph) {
            for (int neighbor : neighbors) {
                file << neighbor << " ";
            }
            file << std::endl;
        }

        return true;
    }

    // Загрузка графа из файла
    bool loadGraphFromFile(const std::string& filename) {
        printDebugInfo("Loading graph from file: " + filename);

        std::ifstream file(filename);
        if (!file.is_open()) {
            std::cerr << "Error: Could not open file " << filename << std::endl;
            return false;
        }

        host_graph.clear();
        std::string line;
        while (std::getline(file, line)) {
            std::istringstream iss(line);
            int neighbor;
            std::vector<int> neighbors;
            while (iss >> neighbor) {
                neighbors.push_back(neighbor);
            }
            host_graph.push_back(neighbors);
        }

        num_points = host_graph.size();
        printDebugInfo("Loaded graph with " + std::to_string(num_points) + " points");

        // Копируем граф на устройство
        device_graph.resize(num_points * k);
        for (int i = 0; i < num_points; ++i) {
            for (int j = 0; j < k; ++j) {
                device_graph[i * k + j] = host_graph[i][j];
            }
        }

        return true;
    }

    // Сброс графа для подмножества точек
    bool resetGraphForSubset(int subset_size) {
        if (subset_size > num_points) {
            std::cerr << "Error: Subset size cannot be larger than total number of points" << std::endl;
            return false;
        }

        printDebugInfo("Resetting graph for subset of size " + std::to_string(subset_size));

        // В реальной реализации здесь должна быть логика сброса графа
        // Например, удаление старых точек и пересчёт графа для новых
        return true;
    }

    // Геттеры и сеттеры
    int getK() const { return k; }
    int getDim() const { return dim; }
    int getNumPoints() const { return num_points; }
    const std::vector<float>& getHostData() const { return host_data; }
    const std::vector<std::vector<int>>& getHostGraph() const { return host_graph; }

    void setDebugMode(bool mode) { debug_mode = mode; }
    void setVerboseQueries(bool mode) { verbose_queries = mode; }
};
```

