## Реализация `knn_graph_wrapper.cpp`

```cpp
#include "knn_graph_wrapper.h"
#include <iostream>
#include <fstream>
#include <vector>
#include <memory>
#include <cuda_runtime.h>
#include <cuda_runtime_api.h>
#include <thrust/device_vector.h>
#include <thrust/host_vector.h>

// Внутренняя реализация
class KNNGraphWrapper::Impl {
public:
    Impl() : dataset_size(0), dimensions(0), k_value(0), debug_mode(false), verbose_query(false) {
        cudaGetDevice(&current_device);
    }

    explicit Impl(int device_id) : dataset_size(0), dimensions(0), k_value(0), 
                                  debug_mode(false), verbose_query(false), current_device(device_id) {
        cudaSetDevice(device_id);
    }

    ~Impl() {
        // Освобождение ресурсов CUDA
        clearDeviceMemory();
    }

    bool loadDataset(const std::string& filename, int dims) {
        if (debug_mode) {
            std::cout << "Loading dataset from: " << filename << std::endl;
        }

        std::ifstream file(filename, std::ios::binary);
        if (!file.is_open()) {
            std::cerr << "Cannot open file: " << filename << std::endl;
            return false;
        }

        // Чтение заголовка (предполагаем бинарный формат)
        file.read(reinterpret_cast<char*>(&dataset_size), sizeof(int));
        file.read(reinterpret_cast<char*>(&dimensions), sizeof(int));

        if (dims > 0 && dimensions != dims) {
            std::cerr << "Dimension mismatch: expected " << dims << ", got " << dimensions << std::endl;
            return false;
        }

        // Чтение данных
        host_data.resize(dataset_size * dimensions);
        file.read(reinterpret_cast<char*>(host_data.data()), 
                 dataset_size * dimensions * sizeof(float));

        file.close();

        // Копирование на GPU
        device_data = host_data;

        if (debug_mode) {
            std::cout << "Loaded dataset: " << dataset_size << " points, " 
                      << dimensions << " dimensions" << std::endl;
        }

        return true;
    }

    bool buildGraph(int k, const std::string& method) {
        if (dataset_size == 0) {
            std::cerr << "No dataset loaded" << std::endl;
            return false;
        }

        k_value = k;

        if (debug_mode) {
            std::cout << "Building KNN graph with k=" << k << " using method: " << method << std::endl;
        }

        // Инициализация графа
        knn_graph.resize(dataset_size * k);
        std::fill(knn_graph.begin(), knn_graph.end(), -1);

        device_knn_graph = knn_graph;

        if (method == "brute") {
            return buildBruteForceKNN();
        } else if (method == "approximate") {
            return buildApproximateKNN();
        } else {
            std::cerr << "Unknown method: " << method << std::endl;
            return false;
        }
    }

    bool refineGraph(int iterations) {
        if (knn_graph.empty()) {
            std::cerr << "KNN graph not built" << std::endl;
            return false;
        }

        if (debug_mode) {
            std::cout << "Refining graph with " << iterations << " iterations" << std::endl;
        }

        return refineGraphKernel(iterations);
    }

    std::vector<int> query(const std::vector<float>& query_point, int k) {
        if (knn_graph.empty()) {
            std::cerr << "KNN graph not built" << std::endl;
            return {};
        }

        if (query_point.size() != dimensions) {
            std::cerr << "Query point dimension mismatch" << std::endl;
            return {};
        }

        if (verbose_query) {
            std::cout << "Querying with k=" << k << std::endl;
        }

        thrust::device_vector<float> d_query(query_point);
        thrust::device_vector<int> d_results(k);

        bool success = queryKernel(thrust::raw_pointer_cast(d_query.data()),
                                 thrust::raw_pointer_cast(d_results.data()),
                                 k);

        if (success) {
            std::vector<int> results(k);
            thrust::copy(d_results.begin(), d_results.end(), results.begin());
            return results;
        }

        return {};
    }

    bool saveGraph(const std::string& filename) {
        if (knn_graph.empty()) {
            std::cerr << "No graph to save" << std::endl;
            return false;
        }

        std::ofstream file(filename, std::ios::binary);
        if (!file.is_open()) {
            std::cerr << "Cannot create file: " << filename << std::endl;
            return false;
        }

        // Запись заголовка
        file.write(reinterpret_cast<const char*>(&dataset_size), sizeof(int));
        file.write(reinterpret_cast<const char*>(&dimensions), sizeof(int));
        file.write(reinterpret_cast<const char*>(&k_value), sizeof(int));

        // Запись графа
        file.write(reinterpret_cast<const char*>(knn_graph.data()),
                  knn_graph.size() * sizeof(int));

        file.close();

        if (debug_mode) {
            std::cout << "Graph saved to: " << filename << std::endl;
        }

        return true;
    }

    bool loadGraph(const std::string& filename) {
        std::ifstream file(filename, std::ios::binary);
        if (!file.is_open()) {
            std::cerr << "Cannot open file: " << filename << std::endl;
            return false;
        }

        // Чтение заголовка
        int saved_size, saved_dims, saved_k;
        file.read(reinterpret_cast<char*>(&saved_size), sizeof(int));
        file.read(reinterpret_cast<char*>(&saved_dims), sizeof(int));
        file.read(reinterpret_cast<char*>(&saved_k), sizeof(int));

        // Проверка совместимости
        if (dataset_size > 0 && (saved_size != dataset_size || saved_dims != dimensions)) {
            std::cerr << "Graph incompatible with current dataset" << std::endl;
            return false;
        }

        dataset_size = saved_size;
        dimensions = saved_dims;
        k_value = saved_k;

        // Чтение графа
        knn_graph.resize(dataset_size * k_value);
        file.read(reinterpret_cast<char*>(knn_graph.data()),
                 knn_graph.size() * sizeof(int));

        // Копирование на GPU
        device_knn_graph = knn_graph;

        file.close();

        if (debug_mode) {
            std::cout << "Graph loaded from: " << filename << std::endl;
        }

        return true;
    }

    bool resetGraph(int new_size) {
        if (new_size <= 0 || new_size > dataset_size) {
            std::cerr << "Invalid new size: " << new_size << std::endl;
            return false;
        }

        if (debug_mode) {
            std::cout << "Resetting graph to size: " << new_size << std::endl;
        }

        // Обновление размера данных
        thrust::host_vector<float> new_host_data(new_size * dimensions);
        std::copy(host_data.begin(), host_data.begin() + new_size * dimensions,
                 new_host_data.begin());

        host_data = new_host_data;
        device_data = host_data;

        // Сброс графа
        dataset_size = new_size;
        knn_graph.clear();
        device_knn_graph.clear();

        return true;
    }

    void printGraphInfo() const {
        std::cout << "KNN Graph Info:" << std::endl;
        std::cout << "  Dataset size: " << dataset_size << std::endl;
        std::cout << "  Dimensions: " << dimensions << std::endl;
        std::cout << "  K value: " << k_value << std::endl;
        std::cout << "  Graph built: " << (!knn_graph.empty() ? "Yes" : "No") << std::endl;
        std::cout << "  GPU device: " << current_device << std::endl;
    }

    void setDebugMode(bool enable) { debug_mode = enable; }
    void setVerboseQuery(bool enable) { verbose_query = enable; }
    void setDevice(int device_id) {
        cudaSetDevice(device_id);
        current_device = device_id;
    }

    int getDatasetSize() const { return dataset_size; }
    int getDimensions() const { return dimensions; }

private:
    // Данные
    thrust::host_vector<float> host_data;
    thrust::device_vector<float> device_data;
    thrust::host_vector<int> knn_graph;
    thrust::device_vector<int> device_knn_graph;

    // Параметры
    int dataset_size;
    int dimensions;
    int k_value;
    int current_device;
    bool debug_mode;
    bool verbose_query;

    // CUDA ядра
    bool buildBruteForceKNN();
    bool buildApproximateKNN();
    bool refineGraphKernel(int iterations);
    bool queryKernel(const float* query, int* results, int k);
    void clearDeviceMemory();
};

// Реализация методов обёртки
KNNGraphWrapper::KNNGraphWrapper() : pImpl(std::make_unique<Impl>()) {}
KNNGraphWrapper::KNNGraphWrapper(int device_id) : pImpl(std::make_unique<Impl>(device_id)) {}
KNNGraphWrapper::~KNNGraphWrapper() = default;

bool KNNGraphWrapper::loadDataset(const std::string& filename, int dimensions) {
    return pImpl->loadDataset(filename, dimensions);
}

bool KNNGraphWrapper::buildGraph(int k, const std::string& method) {
    return pImpl->buildGraph(k, method);
}

bool KNNGraphWrapper::refineGraph(int iterations) {
    return pImpl->refineGraph(iterations);
}

bool KNNGraphWrapper::generateGroundTruth(int k, const std::string& output_file) {
    // Реализация генерации эталонных данных
    return true;
}

std::vector<int> KNNGraphWrapper::query(const std::vector<float>& query_point, int k) {
    return pImpl->query(query_point, k);
}

std::vector<std::vector<int>> KNNGraphWrapper::batchQuery(
    const std::vector<std::vector<float>>& query_points, int k) {

    std::vector<std::vector<int>> results;
    for (const auto& point : query_points) {
        results.push_back(query(point, k));
    }
    return results;
}

bool KNNGraphWrapper::saveGraph(const std::string& filename) {
    return pImpl->saveGraph(filename);
}

bool KNNGraphWrapper::loadGraph(const std::string& filename) {
    return pImpl->loadGraph(filename);
}

bool KNNGraphWrapper::resetGraph(int new_size) {
    return pImpl->resetGraph(new_size);
}

void KNNGraphWrapper::printGraphInfo() const {
    pImpl->printGraphInfo();
}

void KNNGraphWrapper::setDebugMode(bool enable) {
    pImpl->setDebugMode(enable);
}

void KNNGraphWrapper::setVerboseQuery(bool enable) {
    pImpl->setVerboseQuery(enable);
}

void KNNGraphWrapper::setDevice(int device_id) {
    pImpl->setDevice(device_id);
}

int KNNGraphWrapper::getDatasetSize() const {
    return pImpl->getDatasetSize();
}

