## CUDA ядра `knn_kernels.cu`

```cpp
#include <cuda_runtime.h>
#include <thrust/device_vector.h>
#include <thrust/transform.h>
#include <thrust/sequence.h>
#include <thrust/sort.h>
#include <thrust/gather.h>
#include <cmath>

// Ядро для вычисления евклидова расстояния
__global__ void computeDistancesKernel(const float* data, const float* query,
                                      float* distances, int num_points, int dimensions) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx < num_points) {
        float dist = 0.0f;
        for (int d = 0; d < dimensions; d++) {
            float diff = data[idx * dimensions + d] - query[d];
            dist += diff * diff;
        }
        distances[idx] = sqrtf(dist);
    }
}

// Ядро для построения KNN графа методом грубой силы
__global__ void bruteForceKNNKernel(const float* data, int* knn_graph,
                                   int num_points, int dimensions, int k) {
    // Реализация brute-force KNN на GPU
    // Это упрощённая версия - в реальности нужна более оптимизированная реализация
}

extern "C" {
    bool KNNGraphWrapper::Impl::buildBruteForceKNN() {
        // Реализация brute-force построения графа
        int blockSize = 256;
        int numBlocks = (dataset_size + blockSize - 1) / blockSize;

        // Временные буферы
        thrust::device_vector<float> distances(dataset_size);
        thrust::device_vector<int> indices(dataset_size);

        for (int i = 0; i < dataset_size; i++) {
            // Вычисление расстояний от точки i до всех остальных
            computeDistancesKernel<<<numBlocks, blockSize>>>(
                thrust::raw_pointer_cast(device_data.data()),
                thrust::raw_pointer_cast(device_data.data()) + i * dimensions,
                thrust::raw_pointer_cast(distances.data()),
                dataset_size, dimensions);

            cudaDeviceSynchronize();

            // Сортировка по расстояниям
            thrust::sequence(indices.begin(), indices.end());
            thrust::sort_by_key(distances.begin(), distances.end(), indices.begin());

            // Копирование k ближайших соседей (исключая саму точку)
            thrust::copy_n(indices.begin() + 1, k_value, 
                          device_knn_graph.begin() + i * k_value);
        }

        // Копирование обратно на CPU
        knn_graph = device_knn_graph;

        return true;
    }

    bool KNNGraphWrapper::Impl::buildApproximateKNN() {
        // Реализация приближённого построения графа
        // (например, используя иерархические методы)
        return true;
