        return true;
    }

    bool KNNGraphWrapper::Impl::queryKernel(const float* query, int* results, int k) {
        // Реализация запроса на графе
        thrust::device_vector<float> distances(dataset_size);

        int blockSize = 256;
        int numBlocks = (dataset_size + blockSize - 1) / blockSize;

        computeDistancesKernel<<<numBlocks, blockSize>>>(
            thrust::raw_pointer_cast(device_data.data()),
            query,
            thrust::raw_pointer_cast(distances.data()),
            dataset_size, dimensions);

        cudaDeviceSynchronize();

        thrust::device_vector<int> indices(dataset_size);
        thrust::sequence(indices.begin(), indices.end());
        thrust::sort_by_key(distances.begin(), distances.end(), indices.begin());

        thrust::copy_n(indices.begin(), k, results);

        return true;
    }

    void KNNGraphWrapper::Impl::clearDeviceMemory() {
        device_data.clear();
        device_knn_graph.clear();
        thrust::device_vector<float>().swap(device_data);
        thrust::device_vector<int>().swap(device_knn_graph);
    }
