    await this.build();

    this.watcher = chokidar.watch([
      'src/**/*',
      'public/**/*'
    ], {
      ignored: /(^|[\/\\])\../,
      persistent: true
    });

    this.watcher
      .on('change', async (path) => {
        console.log(`\n📁 Файл изменен: ${path}`);
        await this.build();
      })
      .on('add', (path) => {
        console.log(`📁 Файл добавлен: ${path}`);
      })
      .on('unlink', (path) => {
        console.log(`📁 Файл удален: ${path}`);
      });
  }

  async run() {
    if (process.argv.includes('--watch')) {
      await this.watch();
    } else {
      await this.build();
    }
  }
