```cpp
#include "knn_graph_wrapper.h"
#include <iostream>
#include <vector>
#include <random>

int main() {
    try {
        // Создание обёртки с указанием GPU устройства
        KNNGraphWrapper knn_wrapper(0);

        // Включение режима отладки
        knn_wrapper.setDebugMode(true);
        knn_wrapper.setVerboseMode(true);

        // Загрузка набора данных
        if (!knn_wrapper.loadDataset("dataset.bin", 128)) {
            std::cerr << "Failed to load dataset" << std::endl;
            return -1;
        }

        // Построение графа KNN
        if (!knn_wrapper.buildGraph(10, "brute_force")) {
            std::cerr << "Failed to build graph" << std::endl;
            return -1;
        }

        // Уточнение графа
        if (!knn_wrapper.refineGraph(5)) {
            std::cerr << "Failed to refine graph" << std::endl;
            return -1;
        }

        // Сохранение графа
        if (!knn_wrapper.saveGraph("knn_graph.bin")) {
            std::cerr << "Failed to save graph" << std::endl;
            return -1;
        }

        // Вывод информации о графе
        knn_wrapper.printGraphInfo();

        // Генерация тестовых запросов
        std::vector<float> query_points;
        std::random_device rd;
        std::mt19937 gen(rd());
        std::uniform_real_distribution<float> dis(0.0f, 1.0f);

        int num_queries = 100;
        int dimensions = knn_wrapper.getDimensions();
        query_points.resize(num_queries * dimensions);
        for (auto& val : query_points) {
            val = dis(gen);
        }

        // Выполнение запросов
        auto results = knn_wrapper.query(query_points, 5, true);

        std::cout << "Query results for first point: ";
        for (int neighbor : results[0]) {
            std::cout << neighbor << " ";
        }
        std::cout << std::endl;

        // Сброс графа до меньшего размера
        if (!knn_wrapper.resetGraph(50000)) {
            std::cerr << "Failed to reset graph" << std::endl;
            return -1;
        }

        std::cout << "KNN graph operations completed successfully!" << std::endl;

    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return -1;
    }

    return 0;
}
