## build.js
```javascript
import { fileURLToPath } from 'url';
import { dirname, join } from 'path';
import fs from 'fs-extra';
import chokidar from 'chokidar';
import sass from 'sass';
import { marked } from 'marked';
import { minify } from 'uglify-js';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

class BuildSystem {
  constructor() {
    this.config = {
      sourceDir: 'src',
      outputDir: 'dist',
      publicDir: 'public',
      tempDir: '.temp'
    };

    this.watcher = null;
    this.isWatching = false;
  }

  async initialize() {
    console.log('🚀 Инициализация системы сборки...');

    // Создаем необходимые директории
    await this.createDirectories();

    // Очищаем выходные директории при необходимости
    if (process.argv.includes('--clean')) {
      await this.clean();
    }
  }

  async createDirectories() {
    const dirs = [
      this.config.sourceDir,
      this.config.outputDir,
      this.config.publicDir,
      this.config.tempDir,
      join(this.config.publicDir, 'css'),
      join(this.config.publicDir, 'js'),
      join(this.config.publicDir, 'images'),
      join(this.config.publicDir, 'fonts')
    ];

    for (const dir of dirs) {
      await fs.ensureDir(dir);
      console.log(`📁 Создана директория: ${dir}`);
    }
  }

  async clean() {
    console.log('🧹 Очистка выходных директорий...');
    await fs.remove(this.config.outputDir);
    await fs.remove(this.config.tempDir);
    await this.createDirectories();
  }

  // Генерация парсеров
  async generateParsers() {
    console.log('🔧 Генерация парсеров...');

    const parserTemplate = `
// Автоматически сгенерированный парсер
// Создан: ${new Date().toISOString()}

class TemplateParser {
  constructor() {
    this.templates = new Map();
  }

  parse(template, data = {}) {
    return template.replace(/\\{\\{([^}]+)\\}\\}/g, (match, key) => {
      return data[key.trim()] || match;
    });
