  async watch() {
    this.isWatching = true;
    console.log('👀 Запуск режима наблюдения...');

    await this.build();

    this.watcher = chokidar.watch([
      'src/**/*',
      'public/**/*'
    ], {
      ignored: /(^|[/\\])\../, // игнорировать скрытые файлы
      persistent: true
    });

    this.watcher
      .on('change', async (path) => {
        console.log(`📁 Файл изменен: ${path}`);
        await this.build();
      })
      .on('add', async (path) => {
        console.log(`📁 Файл добавлен: ${path}`);
        await this.build();
      })
      .on('unlink', async (path) => {
        console.log(`📁 Файл удален: ${path}`);
        await this.build();
      });
  }

  // Остановка наблюдения
  async stopWatching() {
    if (this.watcher) {
      await this.watcher.close();
      this.isWatching = false;
      console.log('🛑 Режим наблюдения остановлен');
    }
  }
}

// Запуск системы сборки
const buildSystem = new BuildSystem();

// Обработка аргументов командной строки
if (process.argv.includes('--watch')) {
  buildSystem.watch();

  // Обработка сигналов для graceful shutdown
  process.on('SIGINT', async () => {
    console.log('\n🛑 Получен сигнал прерывания...');
    await buildSystem.stopWatching();
    process.exit(0);
  });
} else {
  buildSystem.build();
}
