# Обёртка для алгоритма KNN с использованием CUDA и C++

Вот реализация обёртки для алгоритма KNN с поддержкой GPU через CUDA. Этот код включает загрузку данных, построение графа, запросы, сохранение/загрузку графа и другие функции.

```cpp
#include <iostream>
#include <vector>
#include <fstream>
#include <algorithm>
#include <cmath>
#include <unordered_map>
#include <cuda_runtime.h>
#include <thrust/device_vector.h>
#include <thrust/sort.h>
#include <thrust/iterator/zip_iterator.h>
#include <thrust/iterator/counting_iterator.h>

class KNNWrapper {
private:
    // Параметры графа
    int k;  // количество ближайших соседей
    int dim;  // размерность данных
    int num_points;  // количество точек в графе

    // Данные на CPU
    std::vector<float> data_cpu;
    std::vector<std::vector<int>> graph_cpu;

    // Данные на GPU
    thrust::device_vector<float> data_gpu;
    thrust::device_vector<int> graph_gpu;

    // Временные буферы для вычислений
    thrust::device_vector<float> distances_gpu;
    thrust::device_vector<int> indices_gpu;

    // Флаги для отладки
    bool debug_mode = false;
    bool verbose_queries = false;

    // Вспомогательные функции
    void printDebugInfo(const std::string& message) {
        if (debug_mode) {
            std::cout << "[DEBUG] " << message << std::endl;
        }
    }

    void printVerboseQueryInfo(const std::string& message) {
        if (verbose_queries) {
            std::cout << "[QUERY] " << message << std::endl;
        }
    }

    // Вычисление расстояния между двумя точками
    __device__ float euclideanDistance(const float* a, const float* b, int dim) {
        float sum = 0.0f;
        for (int i = 0; i < dim; ++i) {
            float diff = a[i] - b[i];
            sum += diff * diff;
        }
        return sqrtf(sum);
    }

    // Ядро для построения графа KNN
    __global__ void buildKNNGraphKernel(
        const float* data, int num_points, int dim, int k,
        int* graph, float* distances, int* indices
    ) {
        int idx = blockIdx.x * blockDim.x + threadIdx.x;
        if (idx >= num_points) return;

        // Вычисляем расстояния до всех точек
        for (int i = 0; i < num_points; ++i) {
            distances[i] = euclideanDistance(&data[idx * dim], &data[i * dim], dim);
            indices[i] = i;
        }

        // Сортируем индексы по расстоянию
        thrust::sort_by_key(thrust::device, distances, distances + num_points, indices);

        // Записываем k ближайших соседей (исключая саму точку)
        for (int i = 0; i < k; ++i) {
            if (indices[i] != idx) {
                graph[idx * k + i] = indices[i];
            } else {
                // Если сама точка входит в k ближайших, заменяем на следующую
                graph[idx * k + i] = indices[i + 1];
            }
        }
    }

    // Ядро для запроса KNN
    __global__ void queryKNNKernel(
        const float* data, int num_points, int dim, int k,
        const float* query, int* result, float* result_distances
    ) {
        int idx = blockIdx.x * blockDim.x + threadIdx.x;
        if (idx >= num_points) return;

        // Вычисляем расстояние до текущей точки
        float distance = euclideanDistance(query, &data[idx * dim], dim);

        // Инициализируем результат
        result[idx] = -1;
        result_distances[idx] = INFINITY;

        // Проверяем, является ли точка соседом для запроса
        for (int i = 0; i < k; ++i) {
            int neighbor = graph[idx * k + i];
            float neighbor_distance = euclideanDistance(&data[neighbor * dim], query, dim);

            if (neighbor_distance < result_distances[idx]) {
                result_distances[idx] = neighbor_distance;
                result[idx] = neighbor;
            }
        }
    }

public:
    // Конструктор
    KNNWrapper(int k, int dim) : k(k), dim(dim), num_points(0) {
        printDebugInfo("KNNWrapper initialized with k=" + std::to_string(k) + ", dim=" + std::to_string(dim));
    }

    // Загрузка данных из файла
    void loadData(const std::string& filename) {
        std::ifstream file(filename);
        if (!file.is_open()) {
            throw std::runtime_error("Failed to open file: " + filename);
        }

        // Чтение данных
        data_cpu.clear();
        int temp_dim;
        file >> num_points >> temp_dim;
        if (temp_dim != dim) {
            throw std::runtime_error("Dimension mismatch: expected " + std::to_string(dim) + ", got " + std::to_string(temp_dim));
        }

        for (int i = 0; i < num_points; ++i) {
            for (int j = 0; j < dim; ++j) {
                float value;
                file >> value;
                data_cpu.push_back(value);
            }
        }

        printDebugInfo("Loaded " + std::to_string(num_points) + " points with dimension " + std::to_string(dim));
    }

    // Построение графа KNN
    void buildGraph() {
        if (num_points == 0) {
            throw std::runtime_error("No data loaded. Call loadData() first.");
        }

        // Копируем данные на GPU
        data_gpu = thrust::device_vector<float>(data_cpu.begin(), data_cpu.end());

        // Выделяем память для графа
        graph_cpu.resize(num_points * k);
        graph_gpu.resize(num_points * k);

        // Выделяем временные буферы
        distances_gpu.resize(num_points);
        indices_gpu.resize(num_points);

        // Запускаем ядро для построения графа
        int block_size = 256;
        int grid_size = (num_points + block_size - 1) / block_size;

        buildKNNGraphKernel<<<grid_size, block_size>>>(
            thrust::raw_pointer_cast(data_gpu.data()),
            num_points, dim, k,
            thrust::raw_pointer_cast(graph_gpu.data()),
            thrust::raw_pointer_cast(distances_gpu.data()),
            thrust::raw_pointer_cast(indices_gpu.data())
        );

        // Копируем результат обратно на CPU
        thrust::copy(graph_gpu.begin(), graph_gpu.end(), graph_cpu.begin());

        printDebugInfo("Graph built with " + std::to_string(num_points) + " points and k=" + std::to_string(k));
    }

    // Сохранение графа в файл
    void saveGraph(const std::string& filename) {
        std::ofstream file(filename);
        if (!file.is_open()) {
            throw std::runtime_error("Failed to open file for writing: " + filename);
        }

        file << num_points << " " << dim << " " << k << "\n";
        for (int i = 0; i < num_points; ++i) {
            for (int j = 0; j < k; ++j) {
                file << graph_cpu[i * k + j] << " ";
            }
            file << "\n";
        }

        printDebugInfo("Graph saved to " + filename);
    }

    // Загрузка графа из файла
    void loadGraph(const std::string& filename) {
        std::ifstream file(filename);
        if (!file.is_open()) {
            throw std::runtime_error("Failed to open file: " + filename);
        }

        // Чтение заголовка
        int loaded_num_points, loaded_dim, loaded_k;
        file >> loaded_num_points >> loaded_dim >> loaded_k;

        if (loaded_dim != dim || loaded_k != k) {
            throw std::runtime_error("Graph parameters mismatch");
        }

        num_points = loaded_num_points;
        graph_cpu.resize(num_points * k);
        graph_gpu.resize(num_points * k);

        // Чтение графа
        for (int i = 0; i < num_points; ++i) {
            for (int j = 0; j < k; ++j) {
                file >> graph_cpu[i * k + j];
            }
        }

        // Копируем на GPU
        thrust::copy(graph_cpu.begin(), graph_cpu.end(), graph_gpu.begin());

        printDebugInfo("Graph loaded from " + filename);
    }

    // Запрос KNN для точки
    std::vector<int> queryKNN(const std::vector<float>& query) {
        if (query.size() != dim) {
            throw std::runtime_error("Query dimension mismatch");
        }

        if (num_points == 0) {
            throw std::runtime_error("Graph not built. Call buildGraph() first.");
        }

        // Копируем запрос на GPU
        thrust::device_vector<float> query_gpu(query.begin(), query.end());

        // Выделяем память для результатов
        thrust::device_vector<int> result_gpu(num_points);
        thrust::device_vector<float> result_distances_gpu(num_points);

        // Запускаем ядро запроса
        int block_size = 256;
        int grid_size = (num_points + block_size - 1) / block_size;

        queryKNNKernel<<<grid_size, block_size>>>(
            thrust::raw_pointer_cast(data_gpu.data()),
            num_points, dim, k,
            thrust::raw_pointer_cast(query_gpu.data()),
            thrust::raw_pointer_cast(result_gpu.data()),
            thrust::raw_pointer_cast(result_distances_gpu.data())
        );

        // Копируем результат обратно на CPU
        std::vector<int> result_cpu(num_points);
        thrust::copy(result_gpu.begin(), result_gpu.end(), result_cpu.begin());

        // Фильтруем результаты (удаляем -1)
        std::vector<int> final_result;
        for (int idx : result_cpu) {
            if (idx != -1) {
                final_result.push_back(idx);
            }
        }

        printVerboseQueryInfo("Query returned " + std::to_string(final_result.size()) + " results");
        return final_result;
    }

    // Сброс графа для подмножества данных
    void resetGraphForSubset(int subset_size) {
        if (subset_size > num_points) {
            throw std::runtime_error("Subset size cannot be larger than total points");
        }

        // Обновляем количество точек
        num_points = subset_size;

        // Обрезаем данные
        data_cpu.resize(subset_size * dim);
        data_gpu.resize(subset_size * dim);

        // Обрезаем граф
        graph_cpu.resize(subset_size * k);
        graph_gpu.resize(subset_size * k);

        printDebugInfo("Graph reset for subset of size " + std::to_string(subset_size));
    }

    // Включение/выключение режима отладки
    void setDebugMode(bool enable) {
        debug_mode = enable;
        printDebugInfo("Debug mode " + (enable ? "enabled" : "disabled"));
    }

    // Включение/выключение подробного вывода запросов
    void setVerboseQueries(bool enable) {
        verbose_queries = enable;
        printVerboseQueryInfo("Verbose queries " + (enable ? "enabled" : "disabled"));
    }

    // Получение информации о графе
    void getGraphInfo() {
        std::cout << "Graph Info:\n";
        std::cout << "  Points: " << num_points << "\n";
        std::cout << "  Dimension: " << dim << "\n";
        std::cout << "  k: " << k << "\n";
        std::cout << "  Data on CPU: " << (data_cpu.empty() ? "No" : "Yes") << "\n";
        std::cout << "  Graph on CPU: " << (graph_cpu.empty() ? "No" : "Yes") << "\n";
    }
};

// Пример использования
int main() {
    try {
        // Создаём обёртку с k=5 и размерностью 3
        KNNWrapper knn(5, 3);

        // Включаем отладку
        knn.setDebugMode(true);

        // Загружаем данные
        knn.loadData("data.txt");

        // Строим граф
        knn.buildGraph();

        // Сохраняем граф
        knn.saveGraph("knn_graph.bin");

        // Загружаем граф обратно
        knn.loadGraph("knn_graph.bin");

        // Создаём запрос
        std::vector<float> query = {1.0f, 2.0f, 3.0f};

        // Включаем подробный вывод запросов
        knn.setVerboseQueries(true);

        // Выполняем запрос
        auto result = knn.queryKNN(query);

        // Выводим результат
        std::cout << "Query results:\n";
        for (int idx : result) {
            std::cout << "  " << idx << "\n";
        }

        // Сбрасываем граф для подмножества
        knn.resetGraphForSubset(100);

        // Получаем информацию о графе
        knn.getGraphInfo();

    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
```

## Основные возможности реализации:

1. **Загрузка данных**: Чтение данных из файла с проверкой размерности
2. **Построение графа KNN**: Использование CUDA для эффективного вычисления расстояний
3. **Запросы KNN**: Поиск ближайших соседей для заданной точки
4. **Сохранение/загрузка графа**: Сериализация графа для последующего использования
5. **Работа с подмножествами**: Возможность сбросить граф для подмножества данных
6. **Режимы отладки**: Подробный вывод информации для диагностики
7. **Подробные запросы**: Дополнительная информация о выполнении запросов

## Как использовать:

1. Создайте объект `KNNWrapper` с нужными параметрами k и размерности
2. Загрузите данные с помощью `loadData()`
3. Постройте граф с помощью `buildGraph()`
4. Выполняйте запросы с помощью `queryKNN()`
5. Сохраняйте и загружайте граф по мере необходимости

