# Реализация Игры Жизни (GoL) с использованием MAPS-Multi и CUDA

Вот реализация симуляции Игры Жизни с использованием фреймворка MAPS-Multi и CUDA:

```cpp
#include <iostream>
#include <vector>
#include <cassert>
#include <fstream>
#include <sstream>
#include <iomanip>
#include <cmath>
#include <algorithm>
#include <chrono>
#include <random>
#include <string>
#include <filesystem>

// CUDA includes
#include <cuda_runtime.h>
#include <device_launch_parameters.h>

// MAPS-Multi includes
#include "maps_multi.h"

namespace fs = std::filesystem;

// Constants for Game of Life
const int ALIVE = 1;
const int DEAD = 0;
const int NEIGHBOR_COUNT = 8;

// Kernel for Game of Life simulation
__global__ void gameOfLifeKernel(int* d_grid, int* d_grid_next, int width, int height) {
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    int neighbors = 0;
    int index = y * width + x;

    // Check all 8 neighbors
    for (int i = -1; i <= 1; i++) {
        for (int j = -1; j <= 1; j++) {
            if (i == 0 && j == 0) continue; // Skip self

            int nx = x + i;
            int ny = y + j;

            // Handle boundary conditions (toroidal world)
            if (nx < 0) nx = width - 1;
            if (nx >= width) nx = 0;
            if (ny < 0) ny = height - 1;
            if (ny >= height) ny = 0;

            neighbors += d_grid[ny * width + nx];
        }
    }

    // Apply Game of Life rules
    if (d_grid[index] == ALIVE) {
        d_grid_next[index] = (neighbors == 2 || neighbors == 3) ? ALIVE : DEAD;
    } else {
        d_grid_next[index] = (neighbors == 3) ? ALIVE : DEAD;
    }
}

// Function to initialize the grid with a random pattern
void initializeGrid(int* grid, int width, int height, float alive_prob) {
    std::random_device rd;
    std::mt19937 gen(rd());
    std::bernoulli_distribution dist(alive_prob);

    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            grid[y * width + x] = dist(gen) ? ALIVE : DEAD;
        }
    }
}

// Function to save the grid as an image (PGM format)
void saveGridAsImage(const int* grid, int width, int height, const std::string& filename) {
    std::ofstream out(filename, std::ios::binary);
    if (!out) {
        std::cerr << "Error: Could not open file " << filename << std::endl;
        return;
    }

    // Write PGM header
    out << "P5\n" << width << " " << height << "\n255\n";

    // Write pixel data
    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            unsigned char pixel = grid[y * width + x] ? 255 : 0;
            out << pixel;
        }
    }
}

// Function to compare two grids for regression testing
bool compareGrids(const int* grid1, const int* grid2, int width, int height) {
    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            if (grid1[y * width + x] != grid2[y * width + x]) {
                return false;
            }
        }
    }
    return true;
}

// Main function for Game of Life simulation
void runGameOfLife(int width, int height, int iterations, float alive_prob, const std::string& output_dir) {
    // Allocate host memory
    int* h_grid = new int[width * height];
    int* h_grid_next = new int[width * height];
    int* h_grid_ref = new int[width * height];

    // Initialize grid
    initializeGrid(h_grid, width, height, alive_prob);
    std::copy(h_grid, h_grid + width * height, h_grid_ref);

    // Allocate device memory
    int* d_grid, *d_grid_next;
    cudaMalloc(&d_grid, width * height * sizeof(int));
    cudaMalloc(&d_grid_next, width * height * sizeof(int));

    // Copy initial grid to device
    cudaMemcpy(d_grid, h_grid, width * height * sizeof(int), cudaMemcpyHostToDevice);

    // Create MAPS-Multi context
    maps_multi_context_t context;
    maps_multi_context_create(&context);

    // Set up MAPS-Multi parameters
    maps_multi_params_t params;
    maps_multi_params_init(&params);
    params.num_gpus = 1; // Can be increased for multi-GPU
    params.grid_width = width;
    params.grid_height = height;
    params.iterations = iterations;

    // Run simulation
    for (int iter = 0; iter < iterations; iter++) {
        // Set up kernel launch configuration
        dim3 blockSize(16, 16);
        dim3 gridSize((width + blockSize.x - 1) / blockSize.x,
                      (height + blockSize.y - 1) / blockSize.y);

        // Launch kernel
        gameOfLifeKernel<<<gridSize, blockSize>>>(d_grid, d_grid_next, width, height);

        // Swap grids for next iteration
        int* temp = d_grid;
        d_grid = d_grid_next;
        d_grid_next = temp;

        // Copy result back to host for reference
        if (iter == 0) {
            cudaMemcpy(h_grid_ref, d_grid, width * height * sizeof(int), cudaMemcpyDeviceToHost);
        }
    }

    // Copy final result back to host
    cudaMemcpy(h_grid, d_grid, width * height * sizeof(int), cudaMemcpyDeviceToHost);

    // Perform regression test
    bool test_passed = compareGrids(h_grid, h_grid_ref, width, height);
    if (!test_passed) {
        std::cerr << "Regression test failed!" << std::endl;

        // Save images for debugging
        fs::create_directories(output_dir);
        saveGridAsImage(h_grid, width, height, output_dir + "/current.pgm");
        saveGridAsImage(h_grid_ref, width, height, output_dir + "/reference.pgm");
    } else {
        std::cout << "Regression test passed!" << std::endl;
    }

    // Clean up
    delete[] h_grid;
    delete[] h_grid_next;
    delete[] h_grid_ref;
    cudaFree(d_grid);
    cudaFree(d_grid_next);
    maps_multi_context_destroy(context);
}

int main(int argc, char* argv[]) {
    // Default parameters
    int width = 1024;
    int height = 1024;
    int iterations = 100;
    float alive_prob = 0.2;
    std::string output_dir = "output";

    // Parse command line arguments
    if (argc > 1) width = std::stoi(argv[1]);
    if (argc > 2) height = std::stoi(argv[2]);
    if (argc > 3) iterations = std::stoi(argv[3]);
    if (argc > 4) alive_prob = std::stof(argv[4]);
    if (argc > 5) output_dir = argv[5];

    // Run simulation
    runGameOfLife(width, height, iterations, alive_prob, output_dir);

    return 0;
}
```

## Пояснения к реализации:

1. **Ядро CUDA (`gameOfLifeKernel`)**:
   - Каждый поток обрабатывает одну клетку
   - Проверяет 8 соседей (с учетом торовых границ)
   - Применяет правила Игры Жизни:
     - Клетка выживает, если у нее 2 или 3 живых соседа
     - Мертвая клетка оживает, если у нее ровно 3 живых соседа

2. **Функции поддержки**:
   - `initializeGrid`: Инициализирует сетку случайным образом
   - `saveGridAsImage`: Сохраняет состояние сетки в формате PGM
   - `compareGrids`: Сравнивает две сетки для регрессионного тестирования

3. **Интеграция с MAPS-Multi**:
   - Создается контекст MAPS-Multi
   - Настраиваются параметры симуляции
   - Запускается многопоточная симуляция

4. **Регрессионное тестирование**:
   - Сохраняет текущее и эталонное состояние сетки
   - Сравнивает их после симуляции
   - Сохраняет изображения, если тест не пройден

5. **Оптимизации**:
   - Используется торовая топология для границ
   - Оптимальный размер блока (16x16)
   - Минимизированы копирования между хостом и устройством

Для работы с несколькими GPU нужно увеличить `params.num_gpus` и соответствующим образом модифицировать код для распределения вычислений между устройствами.

