function handWashDishes(dishes) {
    sink = getSink()
    detergent = getDishDetergent()
    sponge = getSpongeOrCloth()
    dryingRack = getDryingRack()
    sprayNozzleAvailable = checkSprayNozzle()

    // Подготовка раковины
    fillSink(sink, waterTemp = "hot")
    addDetergent(sink, detergent)
    agitateWaterToMakeFoam(sink)

    // Предзамачивание сильно загрязнённой посуды
    for dish in dishes {
        if (isHeavilySoiled(dish)) {
            submerge(dish, sink)
        }
    }
    wait(minutes = 5) // время замачивания (при необходимости)

    // Мытьё и ополаскивание
    for dish in dishes {
        // Если замачивался — вынуть из воды
        if (isInSink(dish)) {
            removeFromSink(dish)
        }

        // Намыливание и чистка
        loadSpongeWithSoapyWater(sponge, sink)
        scrub(dish, sponge, include = ["углы", "выемки", "обратная сторона"])

        // Ополаскивание горячей водой
        if (sprayNozzleAvailable) {
            rinseWithSprayer(dish, waterTemp = "hot")
        } else {
            rinseUnderTap(dish, waterTemp = "hot")
        }

        // Сушка на воздухе
        placeOnDryingRack(dish, dryingRack)
    }

    // Ожидание естественной сушки
    while (existsWetItemOnRack(dryingRack)) {
        wait(minutes = 2)
        promoteAirflowAroundRack(dryingRack) // опционально
    }

    // Уборка сухой посуды
    for dryDish in getItemsFromRack(dryingRack) {
        putAway(dryDish, targetCabinet = selectCabinetFor(dryDish))
    }

    // Завершение: слив воды и уборка
    drainSink(sink)
    wipeSurface(sink, cloth = getCleanCloth())
    wipeCountertops(areas = getAdjacentCountertops(), cloth)

    print("Посуду вымыли, высушили и убрали. Рабочая зона очищена.")
}

