class MainViewModel(
    private val repository: PostsRepository
) : ViewModel() {

    private val _posts = MutableLiveData<List<Post>>()
    val posts: LiveData<List<Post>> = _posts

    private val _loading = MutableLiveData<Boolean>()
    val loading: LiveData<Boolean> = _loading

    private val _error = MutableLiveData<Throwable?>()
    val error: LiveData<Throwable?> = _error

    // Fetch posts from repository and update LiveData
    fun loadPosts(forceRefresh: Boolean = false) {
        viewModelScope.launch {
            _loading.value = true
            _error.value = null
            try {
                val data = repository.getPosts(forceRefresh)
                _posts.value = data
            } catch (t: Throwable) {
                _error.value = t
            } finally {
                _loading.value = false
            }
        }
    }

    // Convenience method for pull-to-refresh or manual reload
    fun refresh() = loadPosts(forceRefresh = true)
}
