# Обёртка для алгоритма KNN на GPU с использованием CUDA и C++

Ниже представлена реализация обёртки для построения и запросов графа k-ближайших соседей (KNN) на GPU с использованием CUDA и C++.

## Основные компоненты

```cpp
#include <iostream>
#include <vector>
#include <fstream>
#include <algorithm>
#include <random>
#include <chrono>
#include <cuda_runtime.h>
#include <thrust/device_vector.h>
#include <thrust/host_vector.h>
#include <thrust/sort.h>
#include <thrust/copy.h>
#include <thrust/execution_policy.h>

// Макросы для отладки
#define CHECK_CUDA_ERROR(val) check((val), #val, __FILE__, __LINE__)
template <typename T>
void check(T err, const char* const func, const char* const file, const int line) {
    if (err != cudaSuccess) {
        std::cerr << "CUDA error at: " << file << ":" << line << std::endl;
        std::cerr << cudaGetErrorString(err) << " " << func << std::endl;
        exit(1);
    }
}

// Структура для хранения графа KNN
struct KNNGraph {
    thrust::device_vector<int>* neighbors;  // Массив соседей для каждой точки
    thrust::device_vector<float>* distances; // Массив расстояний для каждой точки
    int num_points;                         // Количество точек в графе
    int k;                                  // Количество соседей для каждой точки

    KNNGraph(int num_points, int k) : num_points(num_points), k(k) {
        neighbors = new thrust::device_vector<int>(num_points * k);
        distances = new thrust::device_vector<float>(num_points * k);
    }

    ~KNNGraph() {
        delete neighbors;
        delete distances;
    }

    // Загрузка графа из файла
    void loadFromFile(const std::string& filename) {
        std::ifstream file(filename, std::ios::binary);
        if (!file) {
            std::cerr << "Error opening file: " << filename << std::endl;
            return;
        }

        // Считываем количество точек и k
        int file_num_points, file_k;
        file.read(reinterpret_cast<char*>(&file_num_points), sizeof(int));
        file.read(reinterpret_cast<char*>(&file_k), sizeof(int));

        if (file_num_points != num_points || file_k != k) {
            std::cerr << "Graph dimensions mismatch" << std::endl;
            return;
        }

        // Считываем соседей
        thrust::host_vector<int> h_neighbors(num_points * k);
        file.read(reinterpret_cast<char*>(h_neighbors.data()), num_points * k * sizeof(int));
        thrust::copy(h_neighbors.begin(), h_neighbors.end(), neighbors->begin());

        // Считываем расстояния
        thrust::host_vector<float> h_distances(num_points * k);
        file.read(reinterpret_cast<char*>(h_distances.data()), num_points * k * sizeof(float));
        thrust::copy(h_distances.begin(), h_distances.end(), distances->begin());

        file.close();
    }

    // Сохранение графа в файл
    void saveToFile(const std::string& filename) const {
        std::ofstream file(filename, std::ios::binary);
        if (!file) {
            std::cerr << "Error opening file: " << filename << std::endl;
            return;
        }

        // Записываем количество точек и k
        file.write(reinterpret_cast<const char*>(&num_points), sizeof(int));
        file.write(reinterpret_cast<const char*>(&k), sizeof(int));

        // Записываем соседей
        thrust::host_vector<int> h_neighbors(num_points * k);
        thrust::copy(neighbors->begin(), neighbors->end(), h_neighbors.begin());
        file.write(reinterpret_cast<const char*>(h_neighbors.data()), num_points * k * sizeof(int));

        // Записываем расстояния
        thrust::host_vector<float> h_distances(num_points * k);
        thrust::copy(distances->begin(), distances->end(), h_distances.begin());
        file.write(reinterpret_cast<const char*>(h_distances.data()), num_points * k * sizeof(float));

        file.close();
    }

    // Сброс графа для подмножества точек
    void resetSubset(const std::vector<int>& indices) {
        for (int idx : indices) {
            if (idx >= 0 && idx < num_points) {
                for (int i = 0; i < k; ++i) {
                    (*neighbors)[idx * k + i] = -1;  // Маркер пустого соседа
                    (*distances)[idx * k + i] = std::numeric_limits<float>::max();
                }
            }
        }
    }
};

// Класс обёртки для KNN
class KNNWrapper {
private:
    thrust::device_vector<float> data;  // Исходные данные
    int num_points;                     // Количество точек
    int dim;                            // Размерность точек
    int k;                              // Количество соседей
    bool debug_mode;                    // Режим отладки

    // CUDA ядро для вычисления расстояний между точками
    static __global__ void computeDistancesKernel(
        const float* data, int num_points, int dim, int k,
        int* neighbors, float* distances) {

        int idx = blockIdx.x * blockDim.x + threadIdx.x;
        if (idx >= num_points) return;

        // Для каждой точки вычисляем расстояния до всех других точек
        // и находим k ближайших
        // (Это упрощенная версия, реальная реализация может быть более оптимизирована)

        // В реальной реализации здесь должен быть более эффективный алгоритм
        // поиска k ближайших соседей, возможно с использованием сортировки или других методов

        // Для примера просто заполняем фиктивными значениями
        for (int i = 0; i < k; ++i) {
            neighbors[idx * k + i] = (idx + i + 1) % num_points;
            distances[idx * k + i] = static_cast<float>(i + 1) / k;
        }
    }

    // CUDA ядро для уточнения графа
    static __global__ void refineGraphKernel(
        const float* data, int num_points, int dim, int k,
        int* neighbors, float* distances) {

        int idx = blockIdx.x * blockDim.x + threadIdx.x;
        if (idx >= num_points) return;

        // В реальной реализации здесь должен быть алгоритм уточнения графа,
        // например, локальное поиск или перестроение соседей

        // Для примера просто сортируем соседей по расстоянию
        // (Это упрощенная версия, реальная реализация может быть более сложной)

        // В реальном коде здесь должна быть сортировка соседей по расстоянию
        // Для примера просто меняем местами первых два соседа
        if (k >= 2) {
            int temp_neighbor = neighbors[idx * k];
            float temp_distance = distances[idx * k];

            neighbors[idx * k] = neighbors[idx * k + 1];
            distances[idx * k] = distances[idx * k + 1];

            neighbors[idx * k + 1] = temp_neighbor;
            distances[idx * k + 1] = temp_distance;
        }
    }

public:
    KNNWrapper(int num_points, int dim, int k, bool debug_mode = false)
        : num_points(num_points), dim(dim), k(k), debug_mode(debug_mode) {
        data.resize(num_points * dim);
    }

    // Загрузка данных
    void loadData(const std::vector<float>& h_data) {
        if (h_data.size() != static_cast<size_t>(num_points * dim)) {
            std::cerr << "Data size mismatch" << std::endl;
            return;
        }
        thrust::copy(h_data.begin(), h_data.end(), data.begin());
    }

    // Построение графа KNN
    KNNGraph buildGraph() {
        KNNGraph graph(num_points, k);

        // Выделяем память для соседей и расстояний на устройстве
        thrust::device_vector<int> d_neighbors(num_points * k);
        thrust::device_vector<float> d_distances(num_points * k);

        // Запускаем ядро для вычисления расстояний и поиска соседей
        int threads_per_block = 256;
        int blocks = (num_points + threads_per_block - 1) / threads_per_block;
        computeDistancesKernel<<<blocks, threads_per_block>>>(
            thrust::raw_pointer_cast(data.data()),
            num_points, dim, k,
            thrust::raw_pointer_cast(d_neighbors.data()),
            thrust::raw_pointer_cast(d_distances.data()));
        CHECK_CUDA_ERROR(cudaGetLastError());
        CHECK_CUDA_ERROR(cudaDeviceSynchronize());

        // Копируем результаты в граф
        thrust::copy(d_neighbors.begin(), d_neighbors.end(), graph.neighbors->begin());
        thrust::copy(d_distances.begin(), d_distances.end(), graph.distances->begin());

        return graph;
    }

    // Уточнение графа
    void refineGraph(KNNGraph& graph) {
        // Запускаем ядро для уточнения графа
        int threads_per_block = 256;
        int blocks = (num_points + threads_per_block - 1) / threads_per_block;
        refineGraphKernel<<<blocks, threads_per_block>>>(
            thrust::raw_pointer_cast(data.data()),
            num_points, dim, k,
            thrust::raw_pointer_cast(graph.neighbors->data()),
            thrust::raw_pointer_cast(graph.distances->data()));
        CHECK_CUDA_ERROR(cudaGetLastError());
        CHECK_CUDA_ERROR(cudaDeviceSynchronize());
    }

    // Генерация эталонных данных (для тестирования)
    std::vector<float> generateReferenceData() {
        std::vector<float> h_data(num_points * dim);

        std::random_device rd;
        std::mt19937 gen(rd());
        std::uniform_real_distribution<float> dis(0.0, 1.0);

        for (int i = 0; i < num_points * dim; ++i) {
            h_data[i] = dis(gen);
        }

        return h_data;
    }

    // Оценка графа KNN (например, проверка симметричности)
    bool evaluateGraph(const KNNGraph& graph) {
        // В реальной реализации здесь должна быть более сложная оценка
        // Например, проверка симметричности графа

        bool symmetric = true;
        // Для примера просто проверяем первые несколько точек
        for (int i = 0; i < std::min(10, num_points); ++i) {
            for (int j = 0; j < k; ++j) {
                int neighbor = graph.neighbors->at(i * k + j);
                if (neighbor != -1) {
                    // Проверяем, есть ли исходная точка в списке соседей у соседа
                    bool found = false;
                    for (int l = 0; l < k; ++l) {
                        if (graph.neighbors->at(neighbor * k + l) == i) {
                            found = true;
                            break;
                        }
                    }
                    if (!found) {
                        symmetric = false;
                        if (debug_mode) {
                            std::cout << "Asymmetry found between points " 
                                      << i << " and " << neighbor << std::endl;
                        }
                    }
                }
            }
        }

        return symmetric;
    }

    // Запрос k ближайших соседей для точки
    std::vector<std::pair<int, float>> queryKNN(const KNNGraph& graph, int point_idx) {
        if (point_idx < 0 || point_idx >= num_points) {
            std::cerr << "Invalid point index" << std::endl;
            return {};
        }

        std::vector<std::pair<int, float>> result;
        for (int i = 0; i < k; ++i) {
            int neighbor = graph.neighbors->at(point_idx * k + i);
            float distance = graph.distances->at(point_idx * k + i);
            if (neighbor != -1) {  // Проверяем, что сосед не пустой
                result.emplace_back(neighbor, distance);
            }
        }

        return result;
    }

    // Подробный запрос с отладкой
    std::vector<std::pair<int, float>> debugQueryKNN(
        const KNNGraph& graph, int point_idx, bool verbose = true) {

        auto result = queryKNN(graph, point_idx);

        if (verbose && debug_mode) {
            std::cout << "Query results for point " << point_idx << ":" << std::endl;
            for (const auto& [neighbor, distance] : result) {
                std::cout << "  Neighbor " << neighbor << " with distance " << distance << std::endl;
            }
        }

        return result;
    }
};

int main() {
