### 2. Ядро CUDA для Игры Жизни

```cpp
// Ядро CUDA для подсчета живых соседей
__global__ void countNeighbors(const bool* grid, int* neighbors, int width, int height) {
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    int count = 0;
    for (int dy = -1; dy <= 1; ++dy) {
        for (int dx = -1; dx <= 1; ++dx) {
            if (dx == 0 && dy == 0) continue; // Пропускаем саму клетку

            int nx = x + dx;
            int ny = y + dy;

            // Обработка границ (торoidalная сетка)
            nx = (nx < 0) ? width - 1 : ((nx >= width) ? 0 : nx);
            ny = (ny < 0) ? height - 1 : ((ny >= height) ? 0 : ny);

            count += grid[ny * width + nx];
        }
    }

    neighbors[y * width + x] = count;
}

// Ядро CUDA для обновления состояния сетки
__global__ void updateGrid(bool* newGrid, const bool* oldGrid, const int* neighbors, int width, int height) {
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    int index = y * width + x;
    int count = neighbors[index];
    bool currentState = oldGrid[index];

    // Правила Игры Жизни
    if (currentState) {
        // Любая живая клетка с двумя или тремя живыми соседями продолжает жить
        newGrid[index] = (count == 2 || count == 3);
    } else {
        // Мертвая клетка с тремя живыми соседями становится живой
        newGrid[index] = (count == 3);
    }
}

// Функция для одного шага симуляции
void stepGameOfLife(Grid& grid) {
    int width = grid.width;
    int height = grid.height;

    // Выделение памяти для счетчика соседей
    int* neighbors;
    cudaMallocManaged(&neighbors, width * height * sizeof(int));

    // Выделение памяти для новой сетки
    bool* newGrid;
    cudaMallocManaged(&newGrid, width * height * sizeof(bool));

    // Определение блоков и гридов для ядер
    dim3 blockDim(16, 16);
    dim3 gridDim((width + blockDim.x - 1) / blockDim.x, 
                 (height + blockDim.y - 1) / blockDim.y);

    // Подсчет соседей
    countNeighbors<<<gridDim, blockDim>>>(grid.data, neighbors, width, height);
    cudaDeviceSynchronize();

    // Обновление сетки
    updateGrid<<<gridDim, blockDim>>>(newGrid, grid.data, neighbors, width, height);
    cudaDeviceSynchronize();

    // Копирование результата обратно в исходную сетку
    cudaMemcpy(grid.data, newGrid, width * height * sizeof(bool), cudaMemcpyDeviceToDevice);

    // Освобождение временной памяти
    cudaFree(neighbors);
    cudaFree(newGrid);
}
