### 3. Реализация для многоGPU с использованием MAPS-Multi

```cpp
// Класс для управления симуляцией на нескольких GPU
class MultiGPUGameOfLife {
private:
    std::vector<Grid> grids;
    std::vector<int> devices;
    int numGPUs;

public:
    MultiGPUGameOfLife(const std::vector<int>& gpuIds, int width, int height) 
        : numGPUs(gpuIds.size()), devices(gpuIds) {

        for (int gpu : devices) {
            cudaSetDevice(gpu);
            grids.emplace_back(width, height);
        }
    }

    // Инициализация всех сеток случайными значениями
    void initializeRandom(float probability = 0.5) {
        for (size_t i = 0; i < grids.size(); ++i) {
            cudaSetDevice(devices[i]);
            grids[i].initializeRandom(probability);
        }
    }

    // Выполнение одного шага симуляции на всех GPU
    void step() {
        for (size_t i = 0; i < grids.size(); ++i) {
            cudaSetDevice(devices[i]);
            stepGameOfLife(grids[i]);
        }
    }

    // Сохранение состояния всех сеток в файлы
    void saveAllGrids(const std::vector<std::string>& filenames) {
        if (filenames.size() != grids.size()) {
            throw std::runtime_error("Number of filenames doesn't match number of grids");
        }

        for (size_t i = 0; i < grids.size(); ++i) {
            cudaSetDevice(devices[i]);
            grids[i].saveToFile(filenames[i]);
        }
    }

    // Сравнение всех сеток с эталонными
    bool compareAllWithReference(const std::vector<Grid>& referenceGrids) {
        if (referenceGrids.size() != grids.size()) {
            return false;
        }

        for (size_t i = 0; i < grids.size(); ++i) {
            cudaSetDevice(devices[i]);
            if (!grids[i].compare(referenceGrids[i])) {
                return false;
            }
        }
        return true;
    }

    // Получить сетку с определенного GPU
    Grid& getGrid(int gpuIndex) {
        if (gpuIndex < 0 || gpuIndex >= numGPUs) {
            throw std::runtime_error("Invalid GPU index");
        }
        return grids[gpuIndex];
    }
};
