### 4. Регрессионные тесты

```cpp
// Функция для создания эталонной сетки для тестирования
Grid createReferenceGrid(int width, int height) {
    Grid refGrid(width, height);

    // Заполняем эталонную сетку определенным паттерном для тестирования
    bool* hostData = new bool[width * height];
    for (int y = 0; y < height; ++y) {
        for (int x = 0; x < width; ++x) {
            // Простой паттерн: каждая вторая клетка живая
            hostData[y * width + x] = ((x + y) % 2 == 0);
        }
    }

    cudaMemcpy(refGrid.data, hostData, width * height * sizeof(bool), cudaMemcpyHostToDevice);
    delete[] hostData;

    return refGrid;
}

// Регрессионный тест
bool runRegressionTest(int width, int height, int numSteps) {
    // Создаем эталонную сетку
    Grid referenceGrid = createReferenceGrid(width, height);

    // Создаем тестовую сетку (на одном GPU для простоты)
    std::vector<int> gpuIds = {0}; // Используем первый GPU
    MultiGPUGameOfLife simulator(gpuIds, width, height);

    // Инициализируем тестовую сетку так же, как эталонную
    cudaSetDevice(0);
    bool* hostData = new bool[width * height];
    cudaMemcpy(hostData, referenceGrid.data, width * height * sizeof(bool), cudaMemcpyDeviceToHost);
    cudaMemcpy(simulator.getGrid(0).data, hostData, width * height * sizeof(bool), cudaMemcpyHostToDevice);
    delete[] hostData;

    // Выполняем несколько шагов симуляции
    for (int i = 0; i < numSteps; ++i) {
        stepGameOfLife(referenceGrid);
        simulator.step();
    }

    // Сравниваем результаты
    bool result = simulator.compareAllWithReference({referenceGrid});

    // Если тест не прошел, сохраняем результаты для анализа
    if (!result) {
        std::vector<std::string> filenames = {"reference_final.txt", "test_final.txt"};
        Grid refCopy = createReferenceGrid(width, height);
        cudaMemcpy(refCopy.data, referenceGrid.data, width * height * sizeof(bool), cudaMemcpyDeviceToDevice);

        cudaSetDevice(0);
        Grid testCopy = simulator.getGrid(0);

        refCopy.saveToFile(filenames[0]);
        testCopy.saveToFile(filenames[1]);

        std::cerr << "Regression test failed. Results saved to files.\n";
    }

    return result;
}
