```cpp
int main() {
    // Настройка используемых GPU
    std::vector<int> gpuIds = {0, 1}; // Используем первые два GPU

    // Параметры симуляции
    const int width = 64;
    const int height = 64;
    const int numSteps = 10;

    try {
        // Создаем симулятор на нескольких GPU
        MultiGPUGameOfLife simulator(gpuIds, width, height);

        // Инициализируем сетки случайными значениями
        simulator.initializeRandom(0.3); // 30% шанс, что клетка будет живая

        // Выполняем симуляцию
        for (int i = 0; i < numSteps; ++i) {
            simulator.step();
            std::cout << "Completed step " << i + 1 << " of " << numSteps << std::endl;
        }

        // Сохраняем результаты
        std::vector<std::string> filenames;
        for (int i = 0; i < gpuIds.size(); ++i) {
            filenames.push_back("grid_gpu_" + std::to_string(i) + ".txt");
        }
        simulator.saveAllGrids(filenames);

        // Выполняем регрессионный тест
        std::cout << "Running regression test..." << std::endl;
        bool testPassed = runRegressionTest(width, height, numSteps);
        std::cout << "Regression test " << (testPassed ? "passed" : "failed") << std::endl;

    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
