    sink.add(dishSoap)
    sink.createFoam()

    // Предварительное замачивание
    for each (dish in dirtyDishes) {
        if (dish.isHeavilyDirty()) {
            dish.soakIn(sink, 5 minutes)
        }
    }

    // Основной цикл мытья
    while (dirtyDishes.isNotEmpty()) {
        currentDish = dirtyDishes.takeFirst()
        sponge = getSponge()

        // Мытье
