# Реализация метода `build` для `TransportBuilder`

Вот реализация метода `build`, который создает HTTP-транспорт для Elasticsearch с указанными возможностями:

```go
package main

import (
	"context"
	"crypto/tls"
	"net"
	"net/http"
	"net/url"
	"time"
)

// Transport представляет HTTP-транспорт для Elasticsearch
type Transport struct {
	client *http.Client
}

// TransportBuilder строитель для создания Transport
type TransportBuilder struct {
	proxyURL          *url.URL
	proxyUser         string
	proxyPass         string
	noProxy           bool
	auth              string // Basic Auth для Elasticsearch
	tlsConfig         *tls.Config
	headers           http.Header
	timeout           time.Duration
}

// NewTransportBuilder создает новый TransportBuilder
func NewTransportBuilder() *TransportBuilder {
	return &TransportBuilder{
		headers: make(http.Header),
	}
}

// WithProxy настраивает прокси с указанным URL
func (b *TransportBuilder) WithProxy(proxyURL string, user, pass string) *TransportBuilder {
	parsedURL, err := url.Parse(proxyURL)
	if err != nil {
		panic(err) // В реальном коде лучше обработать ошибку
	}
	b.proxyURL = parsedURL
	b.proxyUser = user
	b.proxyPass = pass
	return b
}

// WithoutProxy отключает прокси, включая системные
func (b *TransportBuilder) WithoutProxy() *TransportBuilder {
	b.noProxy = true
	return b
}

// WithAuth устанавливает аутентификацию для Elasticsearch
func (b *TransportBuilder) WithAuth(username, password string) *TransportBuilder {
	b.auth = "Basic " + base64.StdEncoding.EncodeToString([]byte(username+":"+password))
	return b
}

// WithTLSConfig устанавливает конфигурацию TLS
func (b *TransportBuilder) WithTLSConfig(tlsConfig *tls.Config) *TransportBuilder {
	b.tlsConfig = tlsConfig
	return b
}

// WithHeader добавляет HTTP-заголовок
func (b *TransportBuilder) WithHeader(key, value string) *TransportBuilder {
	b.headers.Add(key, value)
	return b
}

// WithHeaders добавляет несколько HTTP-заголовков
func (b *TransportBuilder) WithHeaders(headers http.Header) *TransportBuilder {
	for k, v := range headers {
		for _, hv := range v {
			b.headers.Add(k, hv)
		}
	}
	return b
}

// WithTimeout устанавливает глобальный таймаут
func (b *TransportBuilder) WithTimeout(timeout time.Duration) *TransportBuilder {
	b.timeout = timeout
	return b
}

// build создает Transport на основе настроек
func (b *TransportBuilder) build() (*Transport, error) {
	// Создаем транспорт по умолчанию
	transport := &http.Transport{
		Proxy: func(req *http.Request) (*url.URL, error) {
			// Если задано отключение прокси
			if b.noProxy {
				return nil, nil
			}

			// Если задан пользовательский прокси
			if b.proxyURL != nil {
				// Если указаны учетные данные для прокси
				if b.proxyUser != "" && b.proxyPass != "" {
					req.SetBasicAuth(b.proxyUser, b.proxyPass)
				}
				return b.proxyURL, nil
			}

			// Используем системный прокси, если не отключено
			return http.ProxyFromEnvironment(req)
		},
		TLSClientConfig: b.tlsConfig,
	}

	// Создаем HTTP-клиент
	client := &http.Client{
		Transport: transport,
		Timeout:   b.timeout,
	}

	// Создаем обертку для добавления заголовков
	return &Transport{
		client: &http.Client{
			Transport: roundTripper{
				transport: transport,
				headers:   b.headers,
				auth:      b.auth,
			},
			Timeout: b.timeout,
		},
	}, nil
}

// roundTripper обертка для добавления заголовков и аутентификации
type roundTripper struct {
	transport http.RoundTripper
	headers   http.Header
	auth      string
}

func (rt roundTripper) RoundTrip(req *http.Request) (*http.Response, error) {
	// Добавляем заголовки
	for k, v := range rt.headers {
		for _, hv := range v {
			req.Header.Add(k, hv)
		}
	}

	// Добавляем аутентификацию
	if rt.auth != "" {
		req.Header.Set("Authorization", rt.auth)
	}

	return rt.transport.RoundTrip(req)
}

// Для полной реализации нужно импортировать base64
// import "encoding/base64"
```

