class KnnGraphGpu {
public:
    struct Config {
        int k = 32;                 // степень вершины
        int dim = 128;              // размерность вектора
        bool verbose = false;
        bool debug = false;
    };

    KnnGraphGpu(const Config& cfg);
    ~KnnGraphGpu();

    // Загрузка данных: [n][dim] row-major float32
    void loadData(const float* h_data, size_t n);

    // Построить точный k-NN граф (brute-force)
    void buildExactGraph();

    // «Уточнить» граф (заглушка – можно вставить NSG/NN-Descent)
    void refineGraph(int iterations = 5);

    // Сохранить / загрузить граф
    void saveGraph(const std::string& path) const;
    void loadGraph(const std::string& path);

    // Оставить только первые `keep` вершин
    void shrinkTo(size_t keep);

    // Batch-запросы: для каждого query из [nq][dim] вернуть k ближайших
    void search(const float* h_queries, size_t nq,
                int* h_out_idx, float* h_out_dist) const;

    // Доступ из host к графу (только чтение)
