#include "knn_graph.cuh"
#include <thrust/device_vector.h>
#include <thrust/sort.h>
#include <cublas_v2.h>
#include <cuda_runtime.h>
#include <fstream>
#include <iostream>

#define CUDA_CHECK(ans) do { gpuAssert((ans), __FILE__, __LINE__); } while(0)
inline void gpuAssert(cudaError_t code, const char *file, int line) {
   if (code != cudaSuccess) {
      fprintf(stderr,"GPUassert: %s %s %d\n", cudaGetErrorString(code), file, line);
      exit(code);
   }
}

KnnGraphGpu::KnnGraphGpu(const Config& cfg) : cfg_(cfg) {}
KnnGraphGpu::~KnnGraphGpu() {
    if (d_data_) cudaFree(d_data_);
}

void KnnGraphGpu::loadData(const float* h_data, size_t n) {
    n_ = n;
    graph_.resize(n_);
    size_t bytes = n_ * cfg_.dim * sizeof(float);
    CUDA_CHECK(cudaMalloc(&d_data_, bytes));
    CUDA_CHECK(cudaMemcpy(d_data_, h_data, bytes, cudaMemcpyHostToDevice));
    if (cfg_.verbose) std::cout << "Loaded " << n_ << " vectors\n";
}

// brute-force k-NN через GEMM
void KnnGraphGpu::bruteForceKnn() {
    cublasHandle_t handle;
    cublasCreate(&handle);
    const int m = n_;          // число query (сам себе)
    const int k = n_;
    const int lda = cfg_.dim, ldb = cfg_.dim, ldc = m;
    thrust::device_vector<float> d_dist(m * k);
    float alpha = -2.0f, beta = 0.0f;
    // d_dist = -2*X*X^T
    cublasSgemm(handle, CUBLAS_OP_N, CUBLAS_OP_T,
                m, k, cfg_.dim,
                &alpha,
                d_data_, lda,
                d_data_, ldb,
                &beta,
                d_dist.data().get(), ldc);

    // добавляем квадраты норм
    thrust::device_vector<float> d_norm(n_);
    const int BS = 256;
    normKernel<<<(n_+BS-1)/BS, BS>>>(d_data_, d_norm.data().get(), n_, cfg_.dim);
    CUDA_CHECK(cudaDeviceSynchronize());

    addNorms<<<m, 256>>>(d_dist.data().get(), d_norm.data().get(), m, k);
    CUDA_CHECK(cudaDeviceSynchronize());

    // для каждой строки сортируем расстояния и берём top-k
    thrust::device_vector<int> d_idx(m*k);
    thrust::sequence(d_idx.begin(), d_idx.end(),
                     0, 1);
    for(int i = 0; i < m; ++i) {
        thrust::sort_by_key(d_dist.begin() + i*k,
                            d_dist.begin() + (i+1)*k,
                            d_idx.begin() + i*k);
    }
    // копируем индексы в host graph
    thrust::host_vector<int> h_idx = d_idx;
    for(int i = 0; i < m; ++i) {
        graph_[i].assign(h_idx.begin() + i*k, h_idx.begin() + i*k + cfg_.k);
    }
    cublasDestroy(handle);
    if (cfg_.verbose) std::cout << "Brute-force graph built\n";
}

void KnnGraphGpu::buildExactGraph() { bruteForceKnn(); }

void KnnGraphGpu::refineGraph(int iters) {
    if (cfg_.verbose)
        std::cout << "Refine stub: " << iters << " iterations (no-op)\n";
}

void KnnGraphGpu::saveGraph(const std::string& path) const {
    std::ofstream f(path, std::ios::binary);
    if (!f) throw std::runtime_error("cannot write graph");
    size_t sz = graph_.size();
    f.write((char*)&sz, sizeof(sz));
    for (const auto& neigh : graph_) {
        int32_t k = neigh.size();
        f.write((char*)&k, sizeof(k));
        f.write((char*)neigh.data(), k * sizeof(int32_t));
    }
}

void KnnGraphGpu::loadGraph(const std::string& path) {
    std::ifstream f(path, std::ios::binary);
    if (!f) throw std::runtime_error("cannot read graph");
    size_t sz;
    f.read((char*)&sz, sizeof(sz));
    graph_.resize(sz);
    for (auto& neigh : graph_) {
        int32_t k;
        f.read((char*)&k, sizeof(k));
        neigh.resize(k);
        f.read((char*)neigh.data(), k * sizeof(int32_t));
    }
    n_ = sz;
    if (cfg_.verbose) std::cout << "Graph loaded, n=" << n_ << "\n";
}

void KnnGraphGpu::shrinkTo(size_t keep) {
    if (keep >= n_) return;
    graph_.resize(keep);
    n_ = keep;
    // TODO: обрезать и d_data_
    if (cfg_.verbose) std::cout << "Graph shrunk to " << keep << "\n";
}

// простейший поиск: brute-force по подграфу
void KnnGraphGpu::search(const float* h_queries, size_t nq,
                         int* h_out_idx, float* h_out_dist) const {
    // TODO: реализовать на GPU
    // пока stub – CPU brute-force
    for (size_t q = 0; q < nq; ++q) {
        const float* qvec = h_queries + q * cfg_.dim;
        std::vector<std::pair<float,int>> buf;
        for (size_t i = 0; i < n_; ++i) {
            float d = 0;
            for (int j = 0; j < cfg_.dim; ++j) {
                float diff = qvec[j] - ((float*)d_data_)[i*cfg_.dim + j];
                d += diff*diff;
            }
            buf.emplace_back(d, (int)i);
        }
        std::partial_sort(buf.begin(), buf.begin() + cfg_.k,
                          buf.end());
        for (int j = 0; j < cfg_.k; ++j) {
            h_out_idx[q*cfg_.k + j] = buf[j].second;
            h_out_dist[q*cfg_.k + j] = buf[j].first;
        }
    }
}

// kernels
__global__ void normKernel(const float* data, float* out, int n, int dim) {
    int i = blockIdx.x * blockDim.x + threadIdx.x;
    if (i >= n) return;
    float s = 0;
    for (int j = 0; j < dim; ++j) {
        float v = data[i*dim + j];
        s += v*v;
    }
    out[i] = s;
}

__global__ void addNorms(float* dist, const float* norm, int m, int k) {
    int i = blockIdx.x;
    int j = threadIdx.x;
    if (j < k) dist[i*k + j] += norm[i] + norm[j];
}
