// TransportBuilder собирает все параметры, необходимые для построения
// HTTP-транспорта клиента Elasticsearch.
type TransportBuilder struct {
	// адрес кластера
	addresses []string

	// базовая аутентификация клиента
	username string
	password string

	// api-key аутентификация
	apiKey string

	// прокси
	proxyURL      string
	proxyUser     string
	proxyPassword string
	noProxy       bool

	// tls
	caCert []byte
	insecureSkipVerify bool

	// заголовки
	headers map[string]string

	// тайм-аут
	timeout time.Duration
}

// NewTransportBuilder возвращает «чистый» билдер.
func NewTransportBuilder(addresses []string) *TransportBuilder {
	return &TransportBuilder{
		addresses: addresses,
		headers:   make(map[string]string),
		timeout:   90 * time.Second, // умолчание
	}
}

// SetBasicAuth задаёт логин/пароль клиента.
func (b *TransportBuilder) SetBasicAuth(user, pass string) *TransportBuilder {
	b.username, b.password = user, pass
	return b
}

// SetAPIKey задаёт аутентификацию через Api-Key.
func (b *TransportBuilder) SetAPIKey(key string) *TransportBuilder {
	b.apiKey = key
	return b
}

// SetProxy задаёт прокси с опциональной базовой аутентификацией.
func (b *TransportBuilder) SetProxy(rawURL, user, pass string) *TransportBuilder {
	b.proxyURL, b.proxyUser, b.proxyPassword = rawURL, user, pass
	return b
}

// NoProxy отключает любые (в т.ч. системные) прокси.
func (b *TransportBuilder) NoProxy() *TransportBuilder {
	b.noProxy = true
	return b
}

// SetCACert добавляет пользовательский сертификат CA.
func (b *TransportBuilder) SetCACert(pem []byte) *TransportBuilder {
	b.caCert = pem
	return b
}

// InsecureSkipVerify отключает проверку сертификата (для тестов).
func (b *TransportBuilder) InsecureSkipVerify(v bool) *TransportBuilder {
	b.insecureSkipVerify = v
	return b
}

// AddHeader добавляет постоянный заголовок.
func (b *TransportBuilder) AddHeader(key, value string) *TransportBuilder {
	b.headers[key] = value
	return b
}

// SetTimeout задаёт глобальный тайм-аут запросов.
func (b *TransportBuilder) SetTimeout(d time.Duration) *TransportBuilder {
	b.timeout = d
	return b
}

// Build строит и возвращает готовый elasticsearch.Client.
func (b *TransportBuilder) Build() (*elasticsearch.Client, error) {
	// 1. Настраиваем TLS
	tlsCfg := &tls.Config{
		InsecureSkipVerify: b.insecureSkipVerify,
	}

	if len(b.caCert) != 0 {
		pool := x509.NewCertPool()
		if !pool.AppendCertsFromPEM(b.caCert) {
			return nil, errors.New("unable to parse CA certificate")
		}
		tlsCfg.RootCAs = pool
	}

	// 2. Настраиваем транспорт
	tr := &http.Transport{
		TLSClientConfig: tlsCfg,
	}

	switch {
	case b.noProxy:
		tr.Proxy = nil
	case b.proxyURL != "":
		u, err := url.Parse(b.proxyURL)
		if err != nil {
			return nil, err
		}
		if b.proxyUser != "" {
			u.User = url.UserPassword(b.proxyUser, b.proxyPassword)
		}
		tr.Proxy = http.ProxyURL(u)
		// иначе оставляем дефолт (http.ProxyFromEnvironment)
	}

	// 3. HTTP-клиент с нужным тайм-аутом
	httpCli := &http.Client{
		Transport: tr,
		Timeout:   b.timeout,
	}

	// 4. Конфигурация elasticsearch
	cfg := elasticsearch.Config{
		Addresses: b.addresses,
		Transport: httpCli.Transport,
	}

	// аутентификация
	if b.apiKey != "" {
		cfg.APIKey = b.apiKey
	} else if b.username != "" {
		cfg.Username = b.username
		cfg.Password = b.password
	}

	// глобальные заголовки
	if len(b.headers) != 0 {
		cfg.Header = b.headers
	}

	return elasticsearch.NewClient(cfg)
}
